INSERT INTO `ff_vod` VALUES ('365', '20', '谍影迷情/邻家女特工第四季', 'HDTV', '', '', '派珀·佩拉博,克里斯托弗·高哈姆', '未知', 'Annie Walker（Piper Perabo）是一名尚在训练中的初级间谍，最近突然被中央情报局「提拔」为外勤特工。她出众的语言才能或许是上司看重的一个原因，但那不是最主要的－－她的上司似乎对她曾经遇到过的某个人（前男友）更感兴趣。Christopher Gorham扮演一个为中央情报局效命军事情报特工，在一次任务中失去了双眼。他是Annie的导师兼行动负责人。Anne Dudek扮演Annie的姐姐，一位有两个孩子的母亲。Kari Matchett扮演中情局特设特务机构「国内保安局」的负责人。Peter Gallagher扮演她的丈夫Arthur，中情局另一个特设特务机构的负责人。', 'http://175.6.0.111:8058/vfolder//upimage/201307//20130718202500.jpg', '欧美', '', '0', '13', '', '1', '1383714862', '1', '0', '0', '0', '0', '1', '1', '3', '7', 'qvod', '', 'qvod://187621276|E0CDED7CDF440EE21760537A25B56EBE012B71E5|邻家女特工第四季_HDTV_01.rmvb|\rqvod://173434378|776B7526F270F73790CA86E8240C547BDF903A69|邻家女特工第四季_HDTV_02.rmvb|\rqvod://226604423|76EF1CE8C8CA76BFBD90BAFD38DC2F28836245E0|邻家女特工第四季_HDTV_03.rmvb|\rqvod://202939128|8F4AFDBDD50BC94567B77F495B3746589DCA0F51|邻家女特工第四季_HDTV_04.mp4|\rqvod://506053309|7885B69F2AFE4BEA662A4F7D3D019B9492F99DB7|邻家女特工第四季_HDTV_05.mkv|\rqvod://176808589|A54B4E1F0AFB652DE2D2985C2EF62C0602EF86CF|邻家女特工第四季_HDTV_06.rmvb|\rqvod://184341236|39909334788FFBBEAE27850AB6938C52970E9277|邻家女特工第四季_HDTV_07.rmvb|\rqvod://205949889|986135CA1BED6817C17E6D661F8B186FFEBD6796|邻家女特工第四季_HDTV_08.mp4|\rqvod://388273382|A42AF269A13D2C104EB37BE64D865F85D632B81D|邻家女特工第四季_HDTV_09.mp4|\rqvod://190340452|6A2CA554F361A4AF16D2EE539C4AA9CD8264AA93|邻家女特工第四季_HDTV_10.mp4|\rqvod://207196205|0CCACB3314305D97BFBB92FDD34367D098E9B6B2|邻家女特工第四季_HDTV_11.mp4|\rqvod://207196205|0CCACB3314305D97BFBB92FDD34367D098E9B6B2|邻家女特工第四季_HDTV_11.mp4|\rqvod://177241935|7AD140FB43D4D4DCBCD02C13F0B0450AEE7CC234|邻家女特工第四季_HDTV_12.rmvb|\rqvod://178758050|4824F78025C96E83EB49C91CE57FC9E0943604E6|邻家女特工第四季_HDTV_13.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=73114', '', 'D', '', '6.0', '9', '1', '0', '0', '0');
