INSERT INTO `ff_vod` VALUES ('449', '18', '刑警110KG', 'HDTV', '', '', '石冢英彦,高畑淳子,中村俊介,星野真里', '黑泽直辅', '石冢英彦出道31年首次主演电视剧，将主演4月期EX木八『刑事110KG』。他所饰演的主角花泽太郎，是一个本来长年都在派出所工作，突然间被拔擢为\"搜查一课长贴身刑警\"的新人刑警。体重110公斤的外表，让他完全看起来不像是个刑警。然而他将以派出所勤务锻炼出来的敏锐洞察力和对人的观察力，以及他让人毫无紧张感的笑容来追查真相。其他演员方面，高畑淳子饰演拔擢太郎为刑警的新任搜查一课长锦织玲子，竹下景子饰演拥有杀人前科的神秘小锅屋女将茜，中村俊介、星野真里饰演太郎搜查一课的同事。故事采一话完结的单元剧，全集过程中将会慢慢揭晓在茜身边发生的事件的全貌。', 'http://175.6.0.111:8058/vfolder//upimage/201309//20130916201220.jpg', '日本', '', '0', '7', '', '1', '1383714878', '9', '0', '0', '0', '0', '1', '1', '8', '6', 'qvod', '', 'qvod://273486014|F96AE3FF2F3E8C0BB60290A6A9A2F5200D8EA1A7|刑警110KG_HDTV_01.mp4|\rqvod://273773603|C341E379A92395C9F3B2EE6B586F8B28D45C2887|刑警110KG_HDTV_02.mp4|\rqvod://274941920|8E54E96331D96965BA4AFA721B22451DD51031DE|刑警110KG_HDTV_03.mp4|\rqvod://274041013|58367EBBC80A07C31649C1A0F3BEB59D07B15B39|刑警110KG_HDTV_04.mp4|\rqvod://275068923|1729932D17403A5D75F744843522F847A6F7B488|刑警110KG_HDTV_05.mp4|\rqvod://256376933|DBE59BD79067291035E695AE12DFA1EAB3BEFDB9|刑警110KG_HDTV_06.rmvb|\rqvod://271656716|B714DE991F2B4416D43985F21A5E95596F0CE2D7|刑警110KG_HDTV_07.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=74616', '', 'X', '', '6.0', '3', '1', '0', '0', '0');
