INSERT INTO `ff_vod` VALUES ('577', '5', '爱心小天使[微电影]', '', '剧情,青春,微电影', '', '肖和若兰,张钰捷,关少雄', '易莉', '&nbsp;&nbsp; 课时间何小飞做梦被唐老师揪着耳朵叫醒，同桌徐莉莉在旁边幸灾乐祸。老师宣布周五组织去红树林秋游，同学们都很开心，唯独窦豆很苦恼。家长们对此各持己见，窦豆为秋游交钱的事苦恼，何小飞偷偷的帮他垫上。小飞操场罚站的时候两人分享秘密时徐莉莉才得知。这时关少雄又欺负窦豆，被小飞给救了，小飞受了点伤，第二天桌子上放着一个创可贴。彼此的关系也发生了改变。原本不想参加秋游的闪闪也在唐老师的开导下开始转变观念。简介结束', 'http://img.yiyi.cc:88/pic/img/201381513264985930.jpg', '大陆', '普通话发音 中文字幕', '2011', '0', '', '1', '1383715070', '2', '0', '0', '0', '0', '1', '1', '9', '6', 'bdhd$$$letv', '', 'DVD$bdhd://390115007|4D9171082639EC594695459D52A6E2EC|爱心小天使 微电影.flv$$$DVD$2064077', 'xml_15', 'http://www.yiyi.cc/film20/aixinxiaotianshiweidianying/', '', 'A', '', '5.0', '6', '1', '0', '0', '0');
