INSERT INTO `ff_vod` VALUES ('595', '5', '国门之约[微电影]', '', '剧情,青春,微电影', '', '房斌,郑颖,曹雨飞', '', '&nbsp;&nbsp;&nbsp; 故事源于边检警察的真实生活，是首部口岸反偷渡题材侦破微电影。讲述了民警郭蒙在一次执行任务时与偷渡集团斗智斗勇，最终一举破获一起以蛇头为首的有组织的偷渡案件。据悉，该片是由上海边检总站自行创作的反映边检警种特色的微电影，于3月20日正式在上海边检总站实地开机拍摄，4月10日在上海松江某景区完成前期拍摄任务。影片《国门之约》主要在上海各出入境口岸实地取景，影片中的角色边检处长、警官郭蒙和魏诗是上海边检总站民警的缩影，充分展现了上海边检民警戌边卫国，心系群众的情怀。影片于今年7月在上海边检总站首映，公安部给予该片好评，影片具有较高的思想性、艺术性和观赏性，是一部优秀的公安题材影视作品。', 'http://img.yiyi.cc:88/pic/img/20138221259724482.jpg', '大陆', '普通话发音 中文字幕', '2013', '0', '', '1', '1383715073', '0', '0', '0', '0', '0', '1', '1', '3', '7', 'bdhd', '', 'DVD$bdhd://92924566|0FE936F473049E6679B555B0EF35E29A|国门之约 微电影.flv', 'xml_15', 'http://www.yiyi.cc/film20/guomenzhiyueweidianying/', '', 'G', '', '3.0', '1', '1', '0', '0', '0');
