INSERT INTO `ff_vod` VALUES ('617', '5', '集合[微电影]', '', '剧情,记录,微电影', '', '原宁,孙炳南,赵帮帮', '张晓颖,万里', '贾老头年轻的时候率领着自己的连队多次征战，战功赫赫。一辈子就在这样的荣誉和骄傲里渐渐老去，那些在战争中牺牲的战友，他始终无法忘怀，终日生活在对过去战争的回忆和对战友的想念中，垂垂老矣之时已经有点痴痴傻傻，把屋顶的鸽子当做自己的连队，每天集合、点到、解散，乐此不疲，一家人无论如何都劝不住他...', 'http://img.yiyi.cc:88/pic/img/201382811545719857.jpg', '大陆', '普通话发音 中文字幕', '2013', '0', '', '1', '1383715076', '9', '0', '0', '0', '0', '1', '1', '1', '3', 'bdhd$$$letv', '', 'DVD$bdhd://42869844|2357235799F0A4AA5A437A831AF34823|集合 微电影.flv$$$DVD$1882175', 'xml_15', 'http://www.yiyi.cc/film20/jiheweidianying/', '', 'J', '', '6.0', '5', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('618', '5', '哪一站[微电影]', '', '剧情,青春,微电影', '', '李小龙,黄蓉,张乔,于晓丽', '刘全玮', '小川与孟叶是高中就在一起的情侣，大学毕业后，孟叶为了支持小川的事业毅然离开故土来到北京，然而大都市的生活并非一帆风顺，他们的生活逐渐陷入窘境，残酷的现实与孟叶的人生追求形成了交叉线，渐行渐远。而小川始终坚持自己在大城市打拼的基本原则，两人最终分道扬镳。分手后，两个人的事业都发展的不错，但时间久了，他们发现，无论事业怎样飞跃，金钱如何膨胀，都无法灌溉他们那颗无根的心，于是他们决定再次回到对方身边，只要在一起，其他的东西都可以重新来过，只要能在一起，才是幸福。 然而现实给他们开了一个小玩笑，险些让他们擦肩而过简介结束', 'http://img.yiyi.cc:88/pic/img/20138281158838893.jpg', '大陆', '普通话发音 中文字幕', '2013', '0', '', '1', '1383715076', '4', '0', '0', '0', '0', '1', '1', '6', '5', 'bdhd$$$letv', '', 'DVD$bdhd://79996165|C71F5019320B64E8BAE87F39742D8178|哪一站 微电影.flv$$$DVD$1882211', 'xml_15', 'http://www.yiyi.cc/film20/nayizhanweidianying/', '', 'N', '', '1.0', '7', '1', '0', '0', '0');
