INSERT INTO `ff_vod` VALUES ('650', '5', '外星人与限价房[微电影]', '', '喜剧,剧情,搞笑,微电影', '', '李鹏,谢天笑,毛衣哥', '祁又一', '小齐（李鹏饰）是一个小天文所里&ldquo;外星生物探测处&rdquo;的处长，32岁了依然一事无成，所以分不到限价房指标。忽然有一天，外星人真的跟他联系了，他兴奋异常觉得有房结婚的愿望就要实现了，但所长（谢天笑饰）根本不相信。正当小齐向外星人发出降落信号，张开双臂等待外星人降临时，没想到他等来的却是&hellip;&hellip;', 'http://img.yiyi.cc:88/pic/img/20139512484360888.jpg', '大陆', '普通话发音 中文字幕', '2013', '0', '', '1', '1383715082', '8', '0', '0', '0', '0', '1', '1', '9', '5', 'bdhd$$$letv', '', 'DVD$bdhd://62099691|DDF42AD6AD695EC0008C1CF2B48370F9|外星人与限价房 微电影.flv$$$DVD$1598172', 'xml_15', 'http://www.yiyi.cc/film20/waixingrenyuxianjiafangweidianying/', '', 'W', '', '5.0', '1', '1', '0', '0', '0');
