INSERT INTO `ff_vod` VALUES ('366', '4', '新闻当事人[2013]', 'TV', '', '', '孙璞,', '未知', '湖南卫视一改娱乐作风，全力打造深度调查类新闻节目《新闻当事人》。亮出新闻“锐”观点，探寻真相，对话高端，用热情和专业，打造湖南卫视自己的严肃深度新闻。在时代的列车上，每一个人都是乘客，在时代的棋局中，没有人是旁观者，我们都是当事人。《新闻当事人》的记者们努力寻找和触碰那些具体的、生动的、能够与这个时代相互印证的个人。关注人的不同生存状态，关注人的安全、幸福和尊严。栏目关注政府行为、社会事件、灾难、司法案件，文化现象、财经人物，食品安全、医疗教育等方方面面。新闻当事人》还将倾力打造帅气睿智的新人主播——孙璞。孙璞获得湖南卫视7年来的首份主持合约，他用阳光亲和的微笑、理性深刻的思考、客观宽容的态度，赋予《新闻当事人》栏目更加鲜活亮丽的气质形象。《新闻当事人》将于2012年元月1号，重磅出击。在第一期中，《新闻当事人》将聚焦热映电影《金陵十三衩》，专访原著作者严歌苓，聆听这位铿锵舞者的文学心路;记者将走进山西艾滋儿童学校，感受一位平凡校长的济世情怀。', 'http://175.6.0.111:8058/201202/11/20120211151818.jpg', '大陆', '', '0', '20131110', '', '1', '1384334727', '9', '0', '0', '0', '0', '1', '1', '1', '9', 'qvod', '', '20130106$qvod://99796809|CE5F70CA8C2314A95ED4CF8165C84959B7E28DFC|新闻当事人_TV_20130106.rmvb|\r20130113$qvod://99919841|C849248C13055368B8911276EB37D4873BDC6CFB|新闻当事人_TV_20130113.rmvb|\r20130119$qvod://99861744|24EC52C8B78B3C626B386AF68C4D7F8E020B664F|新闻当事人_TV_20130119.rmvb|\r20130126$qvod://99911434|C060CB8908F277F0CE0197368095A61248B62347|新闻当事人_TV_20130126.rmvb|\r20130210$qvod://99812275|5C2336D06B3D79B0AD7228A43A7B2C2BBBF74A2F|新闻当事人_TV_20130210.rmvb|\r20130216$qvod://99783613|F9644C0BED9CE5414C8CFB13A65390D8F48425EB|新闻当事人_TV_20130216.rmvb|\r20130217$qvod://99808365|30F46840B831854EE67040FE74E5393D385EF940|新闻当事人_TV_20130217.rmvb|\r20130223$qvod://99800891|8218A54C24AE1984BBB8CEF28377E192A31E817C|新闻当事人_TV_20130223.rmvb|\r20130224$qvod://97792670|72B0499B65D26F841785D77DAB494F31314892ED|新闻当事人_TV_20130224.rmvb|\r20130302$qvod://99719796|D7BDEDA7F2CA9CB6ECD9416AC055DE096BAD1A28|新闻当事人_TV_20130302.rmvb|\r20130303$qvod://99290720|AB7C1DB38E84BB99A21F6C58E6674A196B84B41C|新闻当事人_TV_20130303.rmvb|\r20130309$qvod://99719238|DFF174451EFAE52B2D872ACD36F62DA1E9EAD1A8|新闻当事人_TV_20130309.rmvb|\r20130316$qvod://99469012|B8A2C3FECCEF5A10C7E101A3DDC9674FD332F867|新闻当事人_TV_20130316.rmvb|\r20130323$qvod://99466643|896E5B64B9C1344C4632414C2EDFD8C2ABFB5EED|新闻当事人_TV_20130323.rmvb|\r20130401$qvod://99197478|5A93B8F3F6CC1D768C830C