INSERT INTO `ff_vod` VALUES ('378', '4', 'Win,Who is next', 'TV', '', '', 'YG旗下艺人', '未知', '韩国YG娱乐公司制作的选秀节目《Win》将于本月23日开播。继BigBang之后，YG公司7年来推出的第一支男子组合将通过该节目评选而出。 YG公司新的男子组合将从两个训练生小组中选出。其中A组由5名平均年龄20岁的成员组成，而B组则由6名平均年龄17岁的成员组成。A组和B组将通过《Win》节目在演唱、舞蹈等方面进行较量，获得观众投票多的一组将被命名为“WINNER”出道。 YG娱乐公司相关负责人表示，《Win》的节目名称既是胜利的含义，也是“WHO IS NEXT?”的缩写，A组和B组中哪一支小队能作为WINNER组合出道，将由100%根据观众投票来决定。这样的选拔方式对YG公司来说是史将由100%根据观众投票来决定。这样的选拔方式对YG公司来说是史无前例的，连YG的工作人员都对这场较量充满了期待。', 'http://175.6.0.111:8058/vfolder//upimage/201308//20130825081713.jpg', '韩国', '', '0', '20131102', '', '1', '1383714862', '8', '0', '0', '0', '0', '1', '1', '5', '7', 'qvod', '', '20130823$qvod://592600060|D0E2C96A79F903619F73A89CBFCA538B75B15C41|Win.Who.is.next_TV_20130823.mkv|\r20130830$qvod://744567334|53A6A3D16B2BD12E8E353D273F282C34482D1B12|Win.Who.is.next_TV_20130830.mkv|\r20130906$qvod://686388693|BC22AC0BA8B2D7AC56560D8D89FA74A79DDD7D26|Win.Who.is.next_TV_20130906.mkv|\r20130913$qvod://701288360|AFFF50FB94AD108C1A0EA63A0D10BBCDD1E30728|Win.Who.is.next_TV_20130913.mkv|\r20130920$qvod://669277035|3C3739F050E284006A5B925F05A8081012A92548|Win.Who.is.next_TV_20130920.mkv|\r20130928$qvod://735157214|B90BF65A7CEA600A0F90B0EFFBDE5AD793E5118B|Win.Who.is.next_TV_20130928.mkv|\r20131004$qvod://889438598|FD71CFC51CE796874EA7F8422659A0F5975B3CD4|Win.Who.is.next_TV_20131004.mkv|\r20131012$qvod://856044952|A20C7727C9D5501A1923C69FD0E6AE912B2EBC3C|Win.Who.is.next_TV_20131012.mkv|\r20131019$qvod://628852423|E263940E3BE06BC2076D638F15EF013F4007980D|Win.Who.is.next_TV_20131019.mkv|\r20131026$qvod://1763370447|FB29501335AEADEB950091C88A0028BB71F1BCFE|Win.Who.is.next_TV_20131026.mkv|\r20131102$qvod://225279117|76F2FBB3929302178E7396DE4CAF5DD63D3187D1|Win.Who.is.next_TV_20131102.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=73957', '', 'W', '', '7.0', '2', '1', '0', '0', '0');
