INSERT INTO `ff_vod` VALUES ('417', '15', '陈云/风云岁月', 'HDTV', '', '', '谢钢,丛珊,王霙,李龙君,李雪锋,李天洋,于洋', '金韬', '重大革命历史题材电视剧《风云岁月》是以陈云同志卓越一生为蓝本，展现陈云同志七十年革命生涯的人物传记电视剧。陈云同志在土地革命、抗日战争和解放战争中，先后担任党和军队的重要领导职务，他不畏艰险，勇挑重担，精心谋划，勤奋工作，为党中央和毛泽东同志制定了一系列重要战略决策，为新民主主义革命的胜利和新中国的诞生，立下了不朽功勋。新中国成立后，陈云同志成为党的第一代领导集体的重要成员。他长期主持全国财政经济工作，为国家社会主义工业化和社会主义经济建设的开创和奠基，做出了卓越贡献。在新时期中国特色社会主义蓬勃发展的伟大进程中，陈云同志是党的第二代领导集体的核心成员，他以无产阶级革命家的胆略、气魄和高超的政治智慧，为党的建设、国家的富强和民族的复兴建立了光辉业绩。', 'http://175.6.0.111:8058/vfolder//upimage/201310//20131020211738.jpg', '大陆', '', '0', '30', '', '1', '1383714869', '5', '0', '0', '0', '0', '1', '1', '6', '7', 'qvod', '', 'qvod://242965923|992152BE05DA06865BC4D1515F9918F0CAF5AE7C|陈云_HDTV_01.rmvb|\rqvod://230097801|DE24A3DDCC02416C03F0DF5A452429EC8565A770|陈云_HDTV_02.rmvb|\rqvod://194802769|43942D8580C4346D9EDA1CA96CF38E2CA49F6543|陈云_TV_03.mp4|\rqvod://195087538|4AF6818443BE23D72347CAE3D1720B0306C2FCAA|陈云_TV_04.mp4|\rqvod://277674904|1B58BA5C401ED30180B11A0EA6FCEBDD3382382E|陈云_TV_05.rmvb|\rqvod://268378886|CB37F51AE99F063AA527616A5CD834E4B0C4BECD|陈云_TV_06.rmvb|\rqvod://84250896|180DEF7789CD58D4E65827AFBD4484A5FB98511E|陈云_TV_07.rmvb|\rqvod://95358679|1E9C1FB7570A7434B34E636104E9E2307D85CFD2|陈云_TV_08.rmvb|\rqvod://79018814|A81B5CF59EF796090DEB3D50AC7C54FD2E8A0250|陈云_TV_09.rmvb|\rqvod://82445099|6348171D16BFAE97ED8E1F5EC88BA9077AD802CC|陈云_TV_10.rmvb|\rqvod://244283912|63AF6A60BCAE108C2A585F6D7F6E71494FB83DCE|陈云_HDTV_11.rmvb|\rqvod://244293606|066FCB5C4FB4278D61B613DE05A1937D93E0CEF2|陈云_HDTV_12.rmvb|\rqvod://244217710|62B48F9DF69995659B84EB2E9C9EEA8273245A8E|陈云_HDTV_13.rmvb|\rqvod://244203261|53C8A53E6FD1DFFF154BA0E30F3E00B158438D60|陈云_HDTV_14.rmvb|\rqvod://268205235|3C7164B37EBE88C1F7C7253051353CF7C6F7974B|陈云_HDTV_15.rmvb|\rqvod://244198937|8EA741FC3AA0FCCE2FD67E3E6ECCE6A505FB0303|陈云_HDTV_16.rmvb|\rqvod://244195880|2AEACEDE175E2AEB553B93827E4B55587453DA75|陈云_HDTV_17.rmvb|\rqvod://244234718|EC30FAACE3D2D0D88173524D7B6EADE3ABAD3A46|陈云_HDTV_18.rmvb|\rqvod://360184229|87C2E68B99C59D6D1B6F4B7DBEFB8