INSERT INTO `ff_vod` VALUES ('419', '12', '冷瞳/冷瞳·贞子再现', 'TS', '', '', '孙祖杨,周秀娜,张诗絮,李璨琛,刘羽琦,黄又南,刘锡明,洪天照', '蒋国权,王柯', '苏氏集团总裁苏光明在别墅遇鬼事件后，一日，苏光明的女儿（周秀娜饰）在别墅中从一台监视的DV中发现前男友钟伟强（孙祖杨 饰）与未婚夫许博文（李璨琛 饰）半夜遭到“鬼抬床”，三人才发现貌似真的一切与鬼有关，随后已死去的房东表弟道出了别墅被诅咒的秘密：凡是入住过这栋别墅的人都离奇死亡，上吊、跳楼、弑妻等，而且更恐怖的说到有尸体被埋在别墅地板下面……', 'http://175.6.0.111:8058/vfolder//upimage/201310//20131019103245.jpg', '大陆', '', '0', '0', '', '1', '1383714874', '4', '0', '0', '0', '0', '1', '1', '2', '3', 'qvod', '', 'qvod://523900542|AF0271F30ECF7B73D3D39009DE10DC1219E9BE64|冷瞳_2013_TS.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=75638', '', '0', '', '3.0', '6', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('420', '16', '巨轮[国语]', 'HDTV', '', '', '萧正楠,陈展鹏,钟嘉欣,田蕊妮,阮兆祥', '王心慰', '乔天生（陈展鹏饰）与罗威信（萧正楠饰）为一对同母异父的兄弟。天生原为香港人，后因父亲乔森（李国麟饰）撕毁出生证明书，两兄弟随着母亲回到佛山。1980年从佛山偷渡来香港，途中失散。哥哥天生独自来到香港，弟弟威信与妈妈周玉梅（李司棋饰）到了澳门，两兄弟怀着对生活的理想，各自在两地建立他们的事业，天生投考香港，威信则由小店主成为澳门饼店商人。经过港澳两地二十多年不同事件后，两兄弟的命运出现意想不到的逆转。\r\n    天生在香港有一要好的女朋友卓静（钟嘉欣饰），惟卓静之母亲刘丽娟（谢雪心饰）是权贵失宠的三姨太，希望从此低调生活，女儿对天生的感情更深厚。丽娟有两个女儿，姊姊卓宁（李亚男饰）和妹妹卓静。卓静是一名时装设计师，天生为圆女友开设自己名牌的心愿，不惜与上流社会勾结，做出种种女友无法接受的事，包括与名流花比奥（贝安琪饰）发生亲密关系。', 'http://175.6.0.111:8058/vfolder//upimage/201309//20130923204817.jpg', '香港', '', '0', '0', '', '1', '1383714874', '8', '0', '0', '0', '0', '1', '1', '4', '5', 'qvod', '', 'qvod://381906638|9011AB4E5D6A16CA8ACAF80356AED51C8C2CFAE3|巨轮.国语_HDTV_01.mp4|\rqvod://384018501|8E57F690785377FAE9BEDD3ABA519646DB4C4325|巨轮.国语_HDTV_02.mp4|\rqvod://381845774|C9C246B1FC3170F4A5EA5D451809988D7A409DEE|巨轮.国语_HDTV_03.mp4|\rqvod://376988228|469232367D6FBC7B28C8B573B2D7BFF9FC233845|巨轮.国语_HDTV_04.mp4|\rqvod://376917684|7EC88B11E77BCD876807277A2A7413E0BF5081D2|巨轮.国语_HDTV_05.mp4|\rqvod://378650732|1E1565A6901070D992B54653FDCCF4143903EBE6|巨轮.国语_HDTV_06.mp4|\rqvod://377614171|DAC2C4748899109644FB27CE8FBE62935A5D9E60|巨轮.国语_HDTV_07.mp4|\rqvod://375388760|17F29DBD9808792390446270B5441835CF1E98BF|巨轮.国语_HDTV_08.mp4|\rqvod://358437189|C89E339719E5B111699598CAD627818E9CA7AB77|巨轮.国语_HDTV_09.mp4|\rqvod://360342465|751FBCC6AC2157ABDE19FFCC4729CBC619F481E1|巨轮.国语_HDTV_10.mp4|\rqvod://360607185|BBE6BB9D679352C84C0DA65F459BCB8B9ED1F3ED|巨轮.国语_HDTV_11.mp4|\rqvod://276669780|1E2C0E624E39691A9CE3E41B74A249D63D6B1F9A|巨轮.国语_HDTV_12.mp4|\rqvod://378054832|A0E1D32863E9B289D8547A2096B660FAD1C084A7|巨轮.国语_HDTV_13.mp4|\rqvod://378640323|5CD2B7B22831A49663A8870F62E99DC22479BB98|巨轮.国语_HDTV_14.mp4|\rqvod://376579682|F76C70956C4C13A2E21443AA0958820D3BE4B891|巨轮.国语_HDTV_15.mp4|\rqvod://274902611|EEEF60950A38ACCA2ECCE092AC0CDBA7664081A1|巨轮.国语_HDTV_16.mp4|\rqvod://274466938|E0A28F12FC0B35BE41324F2D5478E586C974C887|巨轮.国语_HDTV_17.mp4|\rqvod://360693252|E079508B79F