INSERT INTO `ff_vod` VALUES ('732', '16', '心的牵引/儿女之心/儿女的心', 'HDTV', '', '', '颂恩·宋帕山,沃恩吉莱·勒姆威拉伊,Gypsy,Keerati,Mahapreukpong,Singharat,Chanphakdee,Grand,Punwarot,Duaysienklao', '未知', '从国外回到泰国时却得知她的男友王立朋，即将与一位拥有葡萄园百万富翁的妹妹柯薇妮结婚。斐柏查很伤心，于是她到酒吧借酒消愁，却遇到一个坏蛋故意把迷药放在她的饮料里并把她带到了一个酒店企图强奸她，幸好被柯拓(Weir饰)救了，因为斐柏查喝醉了，把柯拓当成了她的男朋友又哭又闹，并表达了自己心中的心中的愤怒和伤心。', 'http://175.6.0.111:8058/vfolder//upimage/201308//20130808215206.jpg', '泰国', '', '0', '17', '', '1', '1384334717', '342', '0', '0', '0', '0', '1', '1', '4', '40', 'qvod', '', 'qvod://459078196|A4B2BAF7D70B72E60D7F41B075A2CA624026C294|心的牵引_HDTV_01.mp4|\rqvod://556104468|0D225D3522178F9BDE1A45F93B5C390093F78C0E|心的牵引_HDTV_02.mp4|\rqvod://520119657|6B3C9E2653FCBB15EC7B1273AF5A0DDF02E7D4F2|心的牵引_HDTV_03.mp4|\rqvod://513578321|02FEF0469168551A8480FEABB3912B17F556D47C|心的牵引_HDTV_04.mp4|\rqvod://535803521|4F7E0AEAE1C520D6E6286FA1BF28159EF12107D4|心的牵引_HDTV_05.mp4|\rqvod://528833595|D10FF7348EEC5DBE22B5FEA42FE78A575330E95A|心的牵引_HDTV_06.mp4|\rqvod://509923689|A427B4CE1853A12A87746430DB12C180E10A02E8|心的牵引_HDTV_07.mp4|\rqvod://504700143|F8C3EFE99A0B5C70EE53835B46FAC3EEC4F8BEE9|心的牵引_HDTV_08.mp4|\rqvod://489043105|EFFF987D409458F27C34F090E390B553E6EDAD6F|心的牵引_HDTV_09.mp4|\rqvod://506978191|AF7A43134BBB36AA95A4FFB6C622A2189BD1E648|心的牵引_HDTV_10.mp4|\rqvod://513993978|39E80FB1409473608F573995B47D5F93EAB0EE93|心的牵引_HDTV_11.mp4|\rqvod://529637146|35CBC070F65176B52CC4C79584EDB0DD9D198725|心的牵引_HDTV_12.mp4|\rqvod://416757700|E45D780205D26D28DD0563CC074AD61E1352942C|心的牵引_HDTV_13.rmvb|\rqvod://490050431|90CC7A80FF0C5CBE838D3C4E8D7A3E953DDE962C|心的牵引_HDTV_14.rmvb|\rqvod://383923099|B9C6AB58FFFB50A5838A058F33CAC6FB489C3D77|心的牵引_HDTV_15.rmvb|\rqvod://421365317|B28C275476C23F7A2FBBE5754EB00D4663F0B0F1|心的牵引_HDTV_16.rmvb|\rqvod://412619812|EB8DCC2B0396E0D924A9246CE1FBD1E30B9D4F98|心的牵引_HDTV_17.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=73558', '', 'X', '', '9.0', '160', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('733', '18', 'LINK', 'HDTV', '', '', '大森南朋,田中丽奈,玉山铁二,米姆拉,田中直树,绫野刚,武田铁矢,黑木瞳,永山绚斗,金子统昭,户次重幸,田丸麻纪,前田旺志郎,,舞羽美海,濑户康史,音尾琢真,池田成志,矢岛健一,萤雪次朗,入江雅人', '深川荣洋', '就职于民间警备公司的前警官寺原健（大森南鹏 饰）在台湾执勤时被紧急召回日本。新任务是民自党干事长松冈孝典的护卫工作。松冈孝典是个凭着直言不讳的措词和热情激昂的演讲赢得众多支持的能干的政治家，但同时也树敌不少。寺原卓越的危机管理能力得到其赏识，在护卫松冈的同时也耳闻目睹了政治的内幕。 另一方面，曾因内部揭发而被贬到高知地方检查厅的检察官樱井聪子（黑木瞳 饰）在前上司那里收到一封以起诉某大人物为目的的任命书。相隔许久回到原单位——东京地检特搜部的她却被视为“出卖同伴的女人”受到周围的冷眼相待。与此同时，东京青叶信用金库支店发生爆炸，运钞车遭遇袭击。 以该事件为契机，寺原、松冈、樱井，与青叶信用金库的职员石川亚纪（田中丽奈 饰）开始产生联系。由于关系网继续扩大，北海道的中学教师高岛昭一（玉山铁二 饰）、东京的护士川本理惠（米姆拉 饰）、大阪服装公司职员泽田勉（田中直树 饰）等人，以意想不到的形式相互产生联系。而这7人又与IT公司社长肖恩（绫野刚 饰）有着怎样的命运交错呢的命运交错呢？', 'http://175.6.0.111:8058/vfolder//upimage/201310//20131010232536.jpg', '日本', '', '0', '5', '', '1', '1384334717', '0', '0', '0', '0', '0', '1', '1', '77', '23', 'qvod', '', 'qvod://451535437|83FFD5F6D3B8B339251C33D72C51EBE71D27BF12|LINK_HDTV_01.mp4|\rqvod://220371162|3116C3BB5AAE8C52756CD6CEFAEB2EC9213429CF|LINK_HDTV_02.rmvb|\rqvod://220419245|7302973D683307037501734F875456AF4EA7CB07|LINK_HDTV_03.rmvb|\rqvod://220967368|9DFB8016CD670A50EA7C8437A47AA7BD09D554B5|LINK_HDTV_04.rmvb|\rqvod://574343739|020BE08EB21CC3EDA003E16F136C771AF95B94F0|LINK_HDTV_05.mkv|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=75343', '', 'L', '', '6.0', '181', '1', '0', '0', '0');
