INSERT INTO `ff_vod` VALUES ('827', '11', '来历不明[高清版]', 'BD', '', '', '陈楚童,蒋梦婕,王往,高鑫,缪婷茹,翟小兴', '陈楚童', '为了观测几年前没能“捕捉”到的神秘天体，被人称之为疯子（陈楚童 饰）的天文观测者驻足荒芜的戈壁山脚，并搭建了一个临时观测站，等待天体运转到特殊角度时进行“捕捉”。 就在临近天体运转到最佳观测角度和时间之际，一场沙尘暴之后，一个似傻非傻来历不明的神秘来者（王往 饰），闯入了疯子的生活中。在得不到此人身份的情形下，疯子暂时收留了他，并给他取了个名字“嗨”。“嗨”的行为古怪，接二连三的出现各种奇怪现象让疯子匪夷所思…… 一个从天而降的物体，最终帮助疯子解开了来历不明者的神秘面纱……', 'http://175.6.0.111:8058/vfolder//upimage/201308//20130822104028.jpg', '大陆', '', '0', '0', '', '1', '1384334743', '392', '0', '0', '0', '0', '1', '1', '18', '44', 'qvod', '', 'qvod://730891507|476125ECB12842CFF69D8854EFD88D30FA98F320|来历不明_2013_BD.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=73899', '', 'L', '', '3.0', '57', '1', '0', '0', '0');
