INSERT INTO `ff_vod` VALUES ('1018', '8', '猎头/再见江湖', '', '', '', '周润发,关之琳,陈欣健', '刘成汉', '越战后，退役越军阮力（周润发饰）辗转来到香港靠杀人为生，期间遇上琳（关之琳饰）并成为情侣。然而一次行动中，阮力奉命要干掉琳之父亲，及后力之上司坚得悉琳对其生疑，故下令阮力杀琳以灭口。阮力带琳逃走，坚下令追杀二人，期间琳得悉阮力乃杀父仇人，伤心欲绝……', 'http://175.6.0.111:8058/200906/02/111945989.jpg', '港台', '', '0', '0', '', '1', '1384344238', '895', '0', '0', '0', '0', '1', '-1', '61', '70', 'qvod', '', 'qvod://345194431|69955E23EA28112ED3EE63E998CD375649C63157|再见江湖_Long.Good.Bye.1982.DVDRip.X264.CN.AAC.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=5945', '', 'L', '', '3.0', '83', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1019', '8', '急冻奇侠', '', '', '', '元奎,元华,张曼玉,廖伟雄,元彪', '霍耀良', '故事灵感来自尼古拉斯梅育导演的再世奇人，描述明朝的淫贼凤三奸杀了公主，御前侍卫方守正负责将他的师兄逮捕归案，在追捕过程中，凤三欲偷取师门至宝黑玉佛，藉此超越时空，但遭方所阻，两人跌下悬崖埋身雪地中。时至现代，被中国地质研究队发现二人尸体被冰封，欲运往美国进行研究，途经香港时误触电线而将二人解冻。方守正遇上不良少女阿玉，做了她的私人保镖，凤三则加入大圈帮为非作歹。方看报发现凤三亦在香港，于是再设法追捕。霍耀良导演将这个穿梭时空的动作喜剧拍得颇为热闹有趣，特技部份亦有可观。元彪与元华的打斗身手俐落，张曼玉则演出了喜趣效果。', 'http://175.6.0.111:8058/200906/03/105105154.jpg', '港台', '', '0', '0', '', '1', '1384344238', '183', '0', '0', '0', '0', '1', '1', '97', '71', 'qvod', '', 'qvod://169383314|722061EEB8F7D67BDA09AD633FE82FC3FFD3B927|急冻奇侠_The.Iceman.Cometh.1989.DVDRip.X264.CN.CD1.rmvb|\rqvod://169958380|0D2DB49214A85639837BD8D8FE8D8F26DCF8C13C|急冻奇侠_The.Iceman.Cometh.1989.DVDRip.X264.CN.CD2.rmvb|\rqvod://167007827|102CB6CCA8544E94740EDD8854F617B90F15671D|急冻奇侠_The.Iceman.Cometh.1989.DVDRip.X264.CN.CD3.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=6224', '', 'J', '', '9.0', '39', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1020', '8', '火烧岛', '', '', '', '成龙,黄炳耀,洪金宝', '朱延平', '描述黄伟为了追查杀死未婚妻父亲的凶手而故意犯罪入狱，在狱中，他碰到了各式各样原因入狱的人。后来监狱长要胁黄伟等四人赴菲律宾押解毒贩，一场混战中将人犯击毙。监狱长以为计谋成功，不料被伟供出他与毒犯挂勾的消息，而且证实了他就是伟要找的凶手。', 'http://175.6.0.111:8058/200906/17/181855635.jpg', '港台', '', '0', '0', '', '1', '1384344238', '835', '0', '0', '0', '0', '1', '1', '100', '70', 'qvod', '', 'qvod://209970605|244A73C8CD518ABFD9EA61148EA074137F8B260F|火烧岛_Island.of.Fire.1990.DVDRip.X264.CN.CD1.rmvb|\rqvod://213315281|BD7E06FFAFFEF8025D8BB28CE3D0A0996AD91804|火烧岛_Island.of.Fire.1990.DVDRip.X264.CN.CD2.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=9966', '', 'H', '', '4.0', '69', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1021', '8', '极速漂移', '', '', '', 'Erin,cahill,阿莱克西娅·芭莱尔,尼古拉斯·阿隆', '阿克塞尔·桑德', '速度……它是快乐，是自由，是冒险，是玩命！住在柏林的四个青年人凯蒂、艾瑞克、尼科尔和迈克为了同一个目标走到一起，速度代表着他们的整个生命，漂移是他们一种生活方式，是证明他们人生价值的唯一方式，每一天每一秒，在汽车里，在大街上，在楼宇间……系上安全带，朋友，极速的时刻，现在就要开始了……极速漂移是一部绚丽的速度与激情，香车与美女的商业大作，影片围绕着赛车将四位年轻人对李湘和契而不舍展现得淋漓尽致，显示与时尚的完美结合，场面与音效的视听震撼，让观众享受极致的电影盛宴。 极速漂移是一部关于速度与激情、香车与美女的商业大作，影片围绕着“赛车”将亲情、友情、爱情以及对理想的契而不舍展现的淋漓尽致，是一部集聚感情和生活的，现实和时尚因素的电影。年轻却不轻浮，沉重但不乏快乐，处处透露着爱情和友情，而这一切都有深似亲情的感情线索贯穿，将目前国内倍受期待的大片。极速漂移会不会再次掀起赛车先锋热谈尚不可知，但大量急速前行、漂移转弯、高速超越等眩目镜头，无疑足够让赛车迷们热血沸腾', 'http://175.6.0.111:8058/200904/16/173511895.jpg', '欧美', '', '0', '0', '', '1', '1384344238', '617', '0', '0', '0', '0', '1', '1', '22', '25', 'qvod', '', 'qvod://426954497|144C35E2E0A18272AFD11FC9D90188198B696576|极速漂移_Fast.Track.No.Limits.2008.DVDRip.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=448', '', 'J', '', '7.0', '179', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1022', '8', '烈火雄心/回火', '', '', '', '库尔特·拉塞尔,威廉·鲍德温,罗伯特·德尼罗', '朗·霍华德', '在布莱恩麦卡费提的童年记忆中，身为消防员的父亲丧生于火场的画面一直挥之不去。长大后的布莱恩追随了父亲和兄长史蒂夫的足迹，也成为消防员，在烈焰中打拼。不过作为菜鸟，布莱恩同史蒂夫矛盾重重，布莱恩努力让自己表现出色，以博取史蒂夫和同事们的认同。 芝加哥突发连环纵火案，不断有人在现场丧命，布莱恩开始展开暗中调查，发现自己已经超越了消防员的职责，要将凶手绳之以法……', 'http://175.6.0.111:8058/200906/17/185806582.jpg', '欧美', '', '0', '0', '', '1', '1384344238', '552', '0', '0', '0', '0', '1', '1', '43', '11', 'qvod', '', 'qvod://180979217|EBE48FA70B8883C5C7503E9481F270BF173BDE33|回火_Backdraft.1991.HDRip.XviD-TLF-CD1.rmvb|\rqvod://189745195|13C6E3E06392EF0201204E752D3913A8A49DD9FA|回火_Backdraft.1991.HDRip.XviD-TLF-CD2.rmvb|\rqvod://197778826|7A4C5979EE4FD6619D8BE387F223AE1E58FB0CAD|回火_Backdraft.1991.HDRip.XviD-TLF-CD3.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=9984', '', 'L', '', '3.0', '117', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1023', '8', '神枪手之死/刺杀神枪侠/刺杀杰西·詹姆斯/叛逆暗杀', '', '', '', '布拉德·皮特,佐伊·丹斯切尔,卡西·阿弗莱克,Adam,Arlukiewicz,Dustin,Bollinger', '安德鲁·多米尼克', '英雄死在了背后开冷枪的懦夫手中，是标榜传奇故事的最经典桥段……但是，毕竟我们生活在现实世界，而不是神话里。', 'http://175.6.0.111:8058/200905/21/092835731.jpg', '欧美', '', '0', '0', '', '1', '1384344238', '109', '0', '0', '0', '0', '1', '1', '7', '22', 'qvod', '', 'qvod://581501830|D94687A478439050D0341E5DBCF0A26BAABF9A1D|神枪手之死_The.Assassination.2007.Blu-Ray.Rip.x264.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=3885', '', '0', '', '2.0', '79', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1024', '8', '霹雳火', '', '', '', '成龙,袁咏仪,王敏德', '陈嘉上', '成龙历年来作品，总是令人刮目，一次比一次更多的惊险、一遍又一遍为求完美的真实演出，都有绝佳票房。这次在剧中为缉拿偷车集团首脑，不惜与赛车高手亡命竞逐。成龙几近十项全能的赛车身手外，除了令人直呼过瘾外，从开始到最后一秒钟紧张、刺激的镜头，着实让人叹为观止阿火从小喜爱赛车，但因家中经济条件差，无法实现他想当一名赛车手的美梦。长大后，他老入日本某大车厂做技术员，负责检验新车给试车手实地试验。可是好景不长，他被诬告炒了鱿鱼。回港后，他在父亲旧车厂当改装工，同时加入了警方查寻非法赛车组织。凭他对车的熟悉，查寻工作得心应手，在执行任务中，阿火认识了女记者艾米，他俩成了一对好朋友。一天，他们发现偷车集团利用非法赛车手运送偷来的名车，集团首脑库克是一名亡命车手。阿火协助警方追捕了库克，但因证据不足，延误了审讯，库克越狱逃跑，并扬言要与阿火在赛车场上一决雌雄。不久，库克加入日本亡命车队，参加仙台国际赛车大赛，阿火用半生的积蓄及朋友们的资助购买了赛车，在日本赛车场上与库克展开了激烈的较量，最终，阿火击败了对手，赢得了赛车冠军，并将职业杀手库克捉拿归案……', 'http://175.6.0.111:8058/200906/03/161455544.jpg', '港台', '', '0', '0', '', '1', '1384344238', '11', '0', '0', '0', '0', '1', '1', '23', '79', 'qvod', '', 'qvod://172836605|FDA0629955DB0F4E63DCC30BD5496B3392373763|霹雳火_Thunderbolt.1995.DVDRip.X264.CN.CD1.rmvb|\rqvod://170418580|9B954D7118F5BBFD69C87D44BC2E4F0B593D068E|霹雳火_Thunderbolt.1995.DVDRip.X264.CN.CD2.rmvb|\rqvod://151999657|6273663675E6932E769D1E6B8DA9F72BE9E95E3B|霹雳火_Thunderbolt.1995.DVDRip.X264.CN.CD3.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=6358', '', 'P', '', '8.0', '182', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1025', '8', '闪灵侠/鬼魂/幽灵奇侠', '', '', '', '盖布瑞·马赫特,塞缪尔·杰克逊,斯嘉丽·约翰逊,伊娃·门德斯', '弗兰克·米勒', '中央城的菜鸟警察丹尼库尔特加布里尔曼奇饰空有满腔的正义感，却就职不久就被中央城的犯罪集团头目“章鱼”萨缪尔L杰克逊饰杀死。就在章鱼要按照自己的道德感清洗中央城时，库尔特死而复生，成为了本领高强的“幽灵”，向他开战。对戴着黑色面具、扎着惹眼的红色领带的“幽灵”来说，最大的问题并不是铲除无恶不作的“章鱼”和他的手下有多么困难，而是他自己总是情不自禁地对美女投降，除了“幽灵”的那个妖艳的女助手希尔肯斯嘉丽约翰逊饰，警察局长的女儿艾伦多兰医生莎拉保尔森和来自法国的珠宝大盗珊德莎拉夫艾娃门德斯饰的美丽和热情都让他五迷三道，不能自拔。', 'http://175.6.0.111:8058/200904/29/100836689.jpg', '欧美', '', '0', '0', '', '1', '1384344238', '671', '0', '0', '0', '0', '1', '1', '14', '20', 'qvod', '', 'qvod://436665344|9D5C1D7C54E403F8B84E0EB77ED5AB0E124F24CC|动作片_闪灵侠DVD.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1033', '', 'S', '', '4.0', '70', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1026', '8', '玉战士', '', '', '', '张静初,托米·艾昂,马库·佩尔托拉,成泰燊', 'Antti-Jussi,Annila', '影玉战士讲述了千年之前的古代中国，只有一位身为铁匠儿子的剑侠（托米艾昂饰）可以杀死祸害世人的恶魔，他在杀死恶魔后可以得到永生而不再轮回为世上俗人。在与恶魔决斗前，剑侠爱上了一位美丽女子（张静初饰）。为了爱， 剑侠放弃了杀死恶魔，希望通过重生去和他深爱的人再次重逢。然而当这样的机会来临，却是在千里之外，千年之后的寒冷的现代芬兰。而这位美丽女子是剑侠的真爱宿命吗？还是她另有所爱？ 在知道他千年前的使命后，他再次成为一位为生而战的战士，为了爱人、为了宿命，也为了找到真实的自我，他决定对抗危险的恶魔！', 'http://175.6.0.111:8058/200904/23/143356282.jpg', '欧美', '', '0', '0', '', '1', '1384344238', '253', '0', '0', '0', '0', '1', '1', '38', '35', 'qvod', '', 'qvod://168926857|3C716EFC382EAF57D4624C9174347EC08FAE5736|玉战士_Jadesoturi.2006.FiNNiSH.rm|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=574', '', 'Y', '', '2.0', '60', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1027', '8', '实尾岛', '', '', '', '薛景求,安圣基,郑在泳', '康佑硕', '20世纪60年代末，南北朝鲜之间局势紧张，双方都展开了疯狂的间谍与刺杀行动。1968年1月，南韩，因谋杀罪名成立而被判死刑的印禅，在行刑前突然被召到拘留室，特种部队军官曾魂告诉印禅，如果不想死的话，可以到西南部荒岛“实尾岛”接受特别军事训练。 与此同时，另外30名死囚也接受了特种部队军官曾魂的条件，在死刑执行前获释，但要被送到宝尾岛执行特别任务。这三十一名死囚乘坐渔船到达实尾岛后，便在军官曾魂的宣布下，组成了以刺杀朝鲜元首金日成为目标的“684北派部队”，并随即接受一连串地狱式的体能和军事训练。 死囚们在训练中，抱着为国家效忠的心态，从而弥补了他们之间的矛盾。在接受艰苦的训练之后，他们被秘密送放朝鲜，开始准备详细周密的刺杀计划。可是，三年后，韩国政府突然改变了政策，“684北派部队”计划被取消。仍然滞留朝鲜的三十一名死囚特别刺杀队员，顿时失去了任何依靠和支持，他们感到被国家出卖，不得不在敌国设法自救…… ■关于影片：根据真实历史事件改编 在1968年，南北朝鲜之间的紧张对峙达到高潮，北朝鲜在军队里成立了一个31人的敢死队，派他们前往韩国汉城刺杀总统朴诚赫（Park Chung-hee）。这个刺杀计划很快被韩国破灭，刺杀敢死队员只剩下一个活了下来，其他的被全部剿灭。这件事立刻震动了韩国上下。 这部韩国岁末动作巨片《宝尾岛》即根据这个历史事件而来。原来，在发生北朝鲜派敢死队刺杀韩国总统事件后，韩国政府为了报复，决定自己也成立一个敢死队，去刺杀北朝鲜的主席金日成。他们秘密派出军官，从死囚犯和驱逐犯中找出恨角色，在荒芜的宝尾岛秘密训练后，将他们派往北朝鲜进行刺杀任务——于是，有了本片开始的故事。 依照这个历史事件，导演康佑硕在把本片拍成动作片的同时，加入了死亡阴影和悲剧色彩，所以，他并没有追究到底谁应该为宝尾岛事件负责，影片感兴趣的是这群抱着对国家忠贞的死囚，如何在死亡逼临下走向自己的悲剧。 曾在《武士》、《醉画仙》等影片里有杰出表演的安圣基，在本片里出演一位性格坚韧的死囚刺杀队员。安圣基是活跃韩国影坛几十年的实力派老演员，我们还记得他在《武士》里扮演的足智多谋的陈笠角色（即是最后一个活下来的韩国武士），他在本片里亦有非常精湛的表现。《宝尾岛》的另一位主演是曾出演过《绿洲》和《开心见性》的薛景求，他在本片里与安圣基的老少实力搭档，相当精彩。 ■拍摄花絮：100亿韩元的巨额投资与地狱训练 对于擅拍通俗喜剧的康佑硕来说，拍摄一部纯粹动作电影，是一次极具挑战的尝试。这位拍出了一系列精彩喜剧并成立了自己的电影公司的导演，初次拍摄《宝尾岛》这部动作片，虽然克服了许多困难，但在最后一个星期却真正遇到了一个问题让他苦恼。 “我试图在影片里告诉观众，这是根据真实历史事件改编的”，他说，“但是，这个想法却根据真实历史事件改编的”，他说，“但是，这个想法却严重的限制了我的想象力，最后搞得我在真实与虚构之间把握不定”。 此外，《宝尾岛》的故事发生在南北朝鲜冷战时期，康佑硕说，他不知道“藏在影片故事背后的政治背景，也对这个不感兴趣”，他只想拍一部纯粹的动作影片，讲述一群死囚的悲剧和人性。 “幸好，拍摄这部《宝尾岛》，我没有资金和时间上的限制”，康佑硕说，“而且，希望拍出一部观众喜欢的动作片的心理，一直鼓励我努力把本片拍完，并且做好每一件事”。“或许有人会问，为什么我要拍摄这部政治气味这么浓的动作电影”，他接着说，“我想，在我们现在这个社会，人们有了很多选择有了自己的判断力，因为，我们经历过了太多的磨难了”。 为了拍摄出真实的荒芜海岛效果，导演康佑硕带着他的剧组在韩国的多个岛屿拍摄，包括宝尾岛，甚至他们还去了新西兰和马耳他取景。毫无疑问，《宝尾岛》对于康佑硕和他的电影公司来说，都是一次重手笔，他们给影片投入了超过100亿韩元的资金。 在影片拍摄完接受媒体访问时，主演安圣基道出了《宝尾岛》另一个艰难之处，即演员们接受的艰苦训练和地狱般的拍摄。“当我知道要和康佑硕导演合作拍动作片时，我就做好了受苦的准备”，今年已经51岁的安圣基说，接着他又开了个玩笑：“当看到影片里那么少描述我的艰苦拍摄画面时，我多少有些失望”，看来，演员们经受的艰苦训练要比我们在银幕上看到的多得多。 ■点评：连续两周占据票房冠军 自从《宝尾岛》在2003年12月24日上映后，立刻击败讲述复仇故事的《老男孩》，夺下当周票房冠军，并迅速掀起新的观影狂潮，接着又在新年初的首个周末，再度击败好莱坞巨片《魔戒3：王者归来》蝉联票房冠军，迎来了新年的开门红。《宝尾岛》的上映改变了近几个月来韩国动作影片萎缩的局面，并且在岁末新年喜剧与古装片争锋下，再次展现了韩国动作片的魅力。 《宝尾岛》讲述的是一群死囚在荒芜海岛的地狱生活，很容易让人想起那部轰动一时的《海岸线》，两者虽然着力表现的主题不同，但同样充满野性与残酷的海岛生活。 沙滩上铁丝网下的训练、淤泥中的搏斗、死囚与教官和死囚之间的矛盾，以及死囚们重新面临死亡时的挣扎与迷茫、团结与背叛，使这部讲述死囚的影片显出格外了格外的动作魅力。尤其是导演加给死囚们的悲剧色彩，让影片从始到终萦绕死亡气息，使这部纯粹的动作片散发出人性的启示。', 'http://175.6.0.111:8058/200906/15/153112077.jpg', '韩国', '', '0', '0', '', '1', '1384344238', '364', '0', '0', '0', '0', '1', '1', '13', '75', 'qvod', '', 'qvod://187976024|BDEF4E748EBD89AF2931EB77259189BCD5B48F5F|实尾岛_shiweidaoCD1.rm|\rqvod://222047085|C67E7F29DB1DB7BC85AE7AC9ACD31D89F33063A0|实尾岛_shiweidaoCD2.rm|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=8995', '', 'S', '', '7.0', '87', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1028', '8', '兄弟[2004]', '', '', '', '陆毅,徐静蕾,张耀扬,柯受良,万梓良', '', '兄弟是一部枪战动作影片，由陆毅、徐静蕾、张耀扬和柯受良等人主演，是 一部讲述上世纪30年代上海滩恩怨情仇的电影，该电影从2003年7月起开始拍摄，到10 月底已经全部拍摄完毕。在兄弟一剧中，柯受良饰演一个叫正哥的角色，不仅贪 婪好色、奸险自私，早期还是杀手行业的中介人。据电影公司方面透露，柯受良在 兄弟中发挥得淋漓中发挥得淋漓尽致，进一步奠定了他在影坛上的地位，成为他电影生涯最有 代表性的电影之一，但可惜也是他的最后一部电影。 从我和爸爸的各界反响来看，徐静蕾应该算是个相当合格的导演。但作为演 员出身，徐静蕾还是忘不了自己的本行，在新片兄弟中，徐静蕾首度牵手陆毅扮 演杀手。也许是因为在香港地区的人气越来越旺，徐静蕾此次参与拍摄的兄弟剧 组吸引了香港诸位主创人员前来加盟。兄弟是一部以亲情为主、爱情为辅的电影， 是一部更多讲述“男人与男人间故事”的戏，片中与徐静蕾演对手戏的号称“优质偶 像”的陆毅与徐静蕾在影片中饰演兄妹，而陆毅更会以“杀手”形象出现，在片中有 不少打斗场面。作为内地两位优秀青年演员的代表，陆毅与徐静蕾的首次联手不免让 人期待', 'http://175.6.0.111:8058/200904/28/111837501.jpg', '大陆', '', '0', '0', '', '1', '1384344238', '207', '0', '0', '0', '0', '1', '1', '39', '62', 'qvod', '', 'qvod://379484160|A2E70797A36F8E19E6914FD0DF96ABBA1924D529|兄弟_兄弟.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=845', '', 'X', '', '9.0', '20', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1029', '8', '枪林恋曲', '', '', '', '陈奕迅,周丽淇,王志文,秦海璐', '郑保瑞', '男护士家锐（陈奕迅饰）和健身中心经理靖侬同居将近三年，为了挽救日渐冷淡 的感情，两人计划到日本旅行。出发前夕，家锐在车尾箱发现浑身是血的悍匪华头 （王志文饰）。华头挟持家锐成功摆脱警方的追捕，并要家锐治疗受枪伤的兄弟。伤 者重伤不治，华头又逼家锐照顾他身怀六甲的妻子华嫂（秦海璐饰）。此时，靖侬为 救家锐找到了初恋情人、当警察的陈力帮忙。警方与华头一伙展开激战……', 'http://175.6.0.111:8058/200904/27/182923439.jpg', '港台', '', '0', '0', '', '1', '1384344238', '263', '0', '0', '0', '0', '1', '1', '19', '74', 'qvod', '', 'qvod://370376704|E9A8059EF470A136628DF3A4A5027BEAEBE62AB9|枪林恋曲.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=813', '', 'Q', '', '8.0', '92', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1030', '8', '幻影车神', '', '', '', '萨加莫尔·斯蒂芬奈,黛安·克鲁格', '路易斯-帕斯卡尔·考维拉尔', '出生于1957年的赛车手米歇尔·维兰特是个对赛车痴迷的赛车天才，尽管家人始 终对他喜欢的赛车运动持反对意见，但是米歇尔始终在追求自己的理想，那就是一定 要取得茫斯24小时拉力挑战赛的冠军。凭借着他的热情和那种天生为了赛车运动而打 造的坚强的体魄，以及冷静的分析判断能力，他终于拿到了这个比赛的冠军。一时间 媒体，和车迷都对他崇拜有加，把他奉为法国赛车的天皇巨星……就在各种鲜花，喜 悦包围着米歇尔的同时，一场阴谋也悄悄的展开了。 别的车队对于米歇尔和他的车队所取得的成绩一直耿耿于怀。维斯·王就是这样 的一个女人，她对米歇尔怀恨在心，因为就是因为米歇尔的出色表现，是的维斯·的 父亲含恨退出赛车界，这次维斯精心策划，在比赛中几乎施展了各种阴谋和手段，不 断的给米歇尔制造险情，就在比赛过程中，米歇尔不断受到王所在不断受到王所在的车队车手的阻挠， 不是一起制造撞车就是刻意围追堵截。加上赛事本身就充满了挑战性和危险。米歇尔 能否在这过程中粉碎这种种阴谋，手段，并且战胜自己取得冠军呢?考验他的时刻道 了……', 'http://175.6.0.111:8058/200904/27/170017858.jpg', '欧美', '', '0', '0', '', '1', '1384344238', '347', '0', '0', '0', '0', '1', '1', '25', '72', 'qvod', '', 'qvod://347104256|36FD46E5EBC319C619EC97B2214E153AAD10640B|幻影车神2.rm|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=792', '', 'H', '', '9.0', '200', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1031', '8', '冷面赤心', '', '', '', '简·德克莱尔,肯·德·布沃,沃纳·德·斯曼特', '艾瑞克·范·洛依', '本片以一位政府官员被谋杀的线索开篇，安特卫普武装警察局派遣最杰出的侦探温克和韦斯特调查此案，力图解开其中的重重悬疑……随着案件中蛛丝马迹的被发现，一位名叫“雷达”的职业杀手逐渐浮出水面。 雷达此时已患上阿兹罕墨记忆消失症，病魔使雷达痛苦地发现，要完成杀手任务变得困难重重，而当他意识到自己被人玩弄于政治权力游戏中时，他决定伸张正义。而温克和韦斯特也在努力设法揭开这个错综复杂的谜局，从而防止杀手的进一步行凶。 最后，正义终于战胜了邪恶，并让这三人决定要完成自己的使使命----消灭安特卫普地下暗杀组织。', 'http://175.6.0.111:8058/200904/24/174922419.jpg', '欧美', '', '0', '0', '', '1', '1384344238', '483', '0', '0', '0', '0', '1', '1', '65', '10', 'qvod', '', 'qvod://352326656|D1F9D7CDAFA74B4EFB60F9CA8535BC74FE136D87|动作_冷面赤心.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=684', '', 'L', '', '7.0', '69', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1032', '8', '绿芥刑警', '', '', '', '广末凉子,让·雷诺', '杰哈·卡胡奇', '哈伯是个嫉恶如仇的强悍警察，他的上司很不欣赏他鲁莽的作风。其实，面恶心善 的哈伯有段不为人知的伤心往事，19年前当他秘密前往日本执行任务时，他遇见了日本 女子惠子，她是他一生的挚爱并娶她为妻，孰料惠子旋即神秘失踪。 20年来哈伯从未对惠子忘情，即便像苏菲亚这样的绝色美女向他示好，他也无动于 衷。一天，有个来自日本的电话告知惠子因癌症过世的死讯，哈伯成为唯一的遗产授予 人。于是哈伯动身前往东京。 20年后再次造访日本，没想到惊奇不断：先是哈伯20年前的同事阿毛，居然还在日 本为法国情报局工作，哈伯也就顺理成章跟着阿毛体验日本；其次，他发现惠子并不是 死于癌症，而是被暗杀；更离谱的是，自己居然有个20岁的女儿。这个怪怪女孩由美， 是个情绪化、赶时髦、行径怪异、爱美又败家的日本少女，而她的银行户头里居然有2 亿美金的巨款。 由于黑道想吞掉由美的钱，所以哈伯得尽力保护她的安全，此时阿毛也无端卷入是 非之中，哈伯只好用自己的方式来惩奸除恶。', 'http://175.6.0.111:8058/200904/28/171124646.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '882', '0', '0', '0', '0', '1', '1', '71', '31', 'qvod', '', 'qvod://383801344|C00DE19FE8DBA51C7CB37A012213AEB382350019|绿芥刑警_绿芥刑警.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=970', '', 'L', '', '5.0', '25', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1033', '8', '生活秀', '', '', '', '陶红,陶泽如,潘粤明', '霍建起', '过夜生活的人最恨什么？最恨白天有人敲门。正在睡觉的来双扬被她哥哥的一阵急 促敲门声惊醒，本想大发雷霆的她却见到自己最心痛的侄儿来金多尔站在面前，便预言 又止了。就在刚刚哥哥和多尔一同做了男性生殖系统的手术，嫂子又到长沙听股票讲座 去了，父子俩没人照顾，理所当然到了单身的双扬家休养，这世界不杀熟杀谁？再说吉 庆街来家的老房子，作为长子来双元也是有权继承的。 吉庆街是武汉闹市华灯阴影处的一道风景，到了夜市，这里灯火通明，卖各种小吃 。说说唱唱，笑笑闹闹，逗乐的逗乐，挣钱的挣钱。居民有意见，政府也屡屡取缔，但 往往第二天夜市更加火爆。来双扬在吉庆街卖鸭颈，她的巧嘴、能干、风韵是吉庆街有 名的，她每天卖15斤鸭颈，当天卖不了的，就有一个叫卓雄洲的男人全部买走，已经有 两年多的时间了，卓雄洲看上了来双扬。 来双扬的母亲在她15岁时就去世了，父亲跟一个寡妇私奔，来双扬从心里恨这个女 人。从此来双扬便挑起抚养弟妹的重担，她把煤炉拎到吉庆街上，拎到吉庆街上，卖起了油炸臭干子， 她是吉庆街的第一个个体户，也是吉庆街的一个偶像，地位非常牢固。来双扬用挣来的 钱盘下了一家10平方米的饭馆，用了弟弟来双久的名字\"久久\"作店名，让久久当老板。 久久生得俊美无比，很有女人缘，但整天游手好闲，终于染上毒瘾被送进了戒毒所。来 双扬的妹妹来双瑗在电台做社会热点节目的特约编辑，一心要曝光吉庆街。 来双元和儿子多尔白吃白住一星期还没有走的意思，双元的妻子小金也没有回家的 意思，来双扬受不了了。以久久酒店的打工妹九妹不愿再给占她便宜的双元送盒饭为借 口叫双元回去，双元却说：这老房子也有他的份。双扬气坏了，明白这是好逸恶劳的嫂 子教唆的，多尔非常爱读书，双扬视若已出，她一心只想挣钱把多尔送出国。来家还有 一间房子让人占了，为了房子的事双扬频频跑房管所，施小恩小惠想早日落实政策，将 来家的两间房子归于她的名下。双扬开始行动了，她主动讨好多年未有联系的继母，给 生活在愧疚中的父亲晚年以安慰，自然父亲就希望将来家的那两间老房作为对双扬多年 照顾弟妹的补偿。同时她又请房管所所长去五星级酒店吃饭，点了很贵的菜，又许诺将 九妹嫁给所长犯花痴病的儿子，既为九妹找了一个“好归宿”，同时又为所长了却一桩 心事。自然房管所所长终于把双扬的问题解决了，双扬又领九妹去戒毒所看望穷途末路 的久久，终于让九妹对他死心而甘心情愿地嫁了房管所所长的花痴儿子。 现在，来双扬要解决的最后一个问题是卓雄洲的关系，总不能让别人买了自己两年 多的鸭颈，什么都不说，吊着人家，时间也太长了。双扬约了卓雄洲到一个临湖的度假 村，但实际状况总没期待的那样好。离异的卓雄洲对家庭，妻子的有着自己的态度:女 人就是这样，谁对她好就非得把自己嫁给谁。可偏偏来双扬就是这样的女人。这就是生 活!生活会把结局告诉你的，结局不用你在事先设想。从此，卓雄洲就再也没有出现在 吉庆街了。不过来双扬的生意，一直都不错。', 'http://175.6.0.111:8058/200904/28/170309433.jpg', '大陆', '', '0', '0', '', '1', '1384344240', '632', '0', '0', '0', '0', '1', '1', '48', '8', 'qvod', '', 'qvod://178055168|6DEBFEC24A1A49671BCBC89815160A8C146FE69C|生活秀1.rmvb|\rqvod://175064064|61E21EB01DE4DDE93C999A661ED979CB47454D6F|生活秀2.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=964', '', 'S', '', '8.0', '171', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1034', '8', '宝贝双龙', '', '', '', '石天龙,张耀扬,苏志威,黄洋,郑志豪,张宝文,周比利', '龙生', '中国的霹雳娇娃 李小龙传奇再现 截拳道武馆的馆主天龙（石天龙饰）是全国散打比赛冠军的有利人选。而他明艳 可人的妹妹晓莉（黄洋饰）正在设计以中化武术绝技“截拳道”为背景的游戏程序， 上司郭威（张耀扬饰）也对她大加追求，就在晓莉事业爱情双得意、截拳道游戏即将 设计完成的一刻，软件神秘被盗！追踪中，晓莉发现意是情人郭威盗窃了她的软件。 震惊之余，晓莉冲去指责郭威的背叛，却遭黑帮动持。利欲熏心的郭威又绑架了天龙 的弟弟（苏志威饰）以相要挟。 大敌当头，同为中华武术人，素有积怨的武林同道醉拳拳师志豪（郑志豪 饰）与 天龙消除干戈、大龙联手，小慧也义不容辞，并肩对敌，三人一路刀光剑影，杀气冲 天……哪料到真正的强敌却是邪恶铁拳郭军（周比利饰），一场生死攸关的龙争虎斗 就此展开……', 'http://175.6.0.111:8058/200904/28/154100065.jpg', '大陆', '', '0', '0', '', '1', '1384344240', '796', '0', '0', '0', '0', '1', '1', '14', '31', 'qvod', '', 'qvod://319846400|F635CDA1BD10FD3D60C9C14058074C0A1DC4CD8B|宝贝双龙（T）.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=925', '', 'B', '', '7.0', '69', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1035', '8', '终棘警探', '', '', '', '西蒙·佩吉,尼克·弗洛斯特', '埃德加·怀特', '尼可拉斯·安吉尔是伦敦大都市警察局一个工作努力常有突出表现的警官，他是如此的优秀以至于警局里的其他人都只能活在他的影子下。于是上司就把他派到了偏远的斯坦福特村庄。这是一个宁静的小村，表面看起来这里和犯罪无缘，这个村子20年来都没有发生过一起谋杀案。 安吉尔一到那里就和丹尼·布特曼成为了工作搭档，布特曼心地善良而又过于热心，还显得十分天真，他是当地警局督察员布特曼的儿子，还是一个忠实的动作电影迷，他认为来自大城市的新搭档安吉尔在真实生活中可能就是一个“坏男孩”。安吉尔的到来也让布特曼有了机会体验真实枪战和汽车追捕的刺激，而这也正是他长期梦寐以求的。同时安吉尔还努力做自我调整以适应这种宁静无事的乡村生活。尽管短时间内他也查处了几个很可能被忽视的违法犯罪案件（例如商店偷盗、未成年饮酒和一农夫在其粮仓内私藏大量非法武器），然而安吉尔不久就发现他最需要紧急解决的问题却和一只从主人家中逃离出来的天鹅有关。面对现实，安吉尔和丹尼最终在当地一个小酒馆中团结起来决定铲除“不法之徒”。 在安吉尔抵达这个村庄不久，这里就接连发生了一系列震惊村子的可怕事故，这些坏事好像是由一个戴着斗篷穿者披风的人干的。然而随着时间的流失，斯坦福德的治安好像并不是看起来的那样可怕，于是安吉尔也开始和警局里的其他人发生冲突，尤其是警队里的两个侦探更觉得安吉尔只尔只不过是一名紧张不安的城市警察罢了，他对村子里的异常动静过于敏感。尽管如此，安吉尔却拒绝停止他的调查，他起初怀疑斯特福德当地一家超市的阴险老板西蒙·斯金纳涉嫌谋杀，原因是受害者参与了一个利润丰厚的商品买卖，而这损害了斯金纳的利益，然而当安吉尔和斯金纳的当面对质却只能说明斯金纳表面是清白的，而这件事也让安吉尔的同事对他的信任度进一步降低……', 'http://175.6.0.111:8058/200904/30/103334532.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '786', '0', '0', '0', '0', '1', '1', '49', '27', 'qvod', '', 'qvod://329905152|F39AF5F2093421A26F2C5047683286FC47A32359|终棘警探.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1268', '', 'Z', '', '5.0', '148', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1036', '8', '三角洲部队闹剧/拉里参军记/三脚猫部队', '', '', '', '丹尼·特乔,马利索·妮可,DJ·考尔斯,凯斯·大卫,王牌接线员拉里', 'C·B·哈丁', '拉里“接线员”拉里饰可谓倒霉透顶，除了丢掉工作外，女友也在同一天离他而去，这让他伤心不已。于是，拉里决定同邻居比尔比尔恩格瓦尔饰和从小打到大的“发小”埃弗雷特奎尔斯饰周末出去开怀畅饮，潇洒轻松一下。但是，三个家伙的运气可是坏到了极点，他们途中被蛮横无理的陆军预备役部队军官科林格罗夫凯思戴维饰截住，误认为是开小差的士兵，于是被死拉硬拽上了飞往伊拉克费卢杰的飞机。更好笑的是，他们坐在“悍马”军车上被阴差阳错投放到墨西哥。他们自以为已经来到中东，误打误撞地来到一个小镇，将村民从暴力统治中解救出来，糊里糊涂被当地人当作了大英雄。但当残暴的军阀卡洛斯桑塔纳丹尼特乔率领大军反击时，拉里、比尔和埃弗雷特只得放下手中啤酒，拿起武器，奋起保卫村民，也保护自己，欲证明他们不仅仅是士兵……', 'http://175.6.0.111:8058/200904/29/184450480.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '691', '0', '0', '0', '0', '1', '1', '14', '57', 'qvod', '', 'qvod://364292096|45B5AFBC7D959FEBD96C55850BC4D68D366A2172|拉里参军记.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1254', '', 'S', '', '2.0', '75', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1037', '8', '巴比伦新纪元', '', '', '', '范·迪塞尔,杨紫琼,杰拉尔·德帕迪约', '马修·卡索维茨', '故事发生在并不遥远的未来世界，随着科技的超速发展，人类社会结构也随之而发生了巨大的变化。城市因为科技的发展而变得越来越先进，但同时不为人知的黑暗角落也越来越多。大量高科技的机器人投入使用，为人类提供便利的同时也取代了大批劳动力。很快机器人的数量就已与人类不相上下，世界变成了机器与人类共生的一个团体。主角图洛普(范·迪塞尔 Vin Diesel 饰)是一名退役的老兵，退役的生涯显然并不富裕，也不快乐，他空有一身的武艺却终日无所事事。不得已的情况下他转业当上了拿钱办事的雇佣兵，专门接受各种护送任务。 一天，犹如往常一般，图洛普接手了一项最新的任务。这一任务看似并没有任何的特别之处，不过是在另一名女性护送人员的陪同下，押解一个神秘女人横跨欧亚大陆。当图洛普第一次在修道院门口见到这位女性时，他就知道，这绝不是一个普通的女人，她那美丽的面庞和忧伤的眼神中饱含着深深的哀怨。他的预感并没有出错，护送路途并不平常，很快麻烦便接踵而至。不但官方的警察开始不断地找他们的麻烦，随着一步步地深入了解，他竟然发现还有一个邪教组织也正对她虎视眈眈、意图不轨，因为这个组织相信传说中的救世主弥赛亚将会降临人世，并给人世间带来巨大的变革。而他们相信这个女人就是弥赛亚的孕育体，只不过必须要通过基因改良创造出全新人种来促使弥赛亚的降临！一路上图洛普和同样武艺高强的瑞贝卡修女(杨紫琼 Michelle Yeoh 饰)过关斩将、数度化险为夷，但最终他才知道，原来这个女人身上还有种恐怖的病毒，其威力足以毁灭全部人类！这一切的一切都使图洛普开始怀疑这位女性的身世，以及她背后那隐约可见的强大势力集团。虽然雇佣兵收钱办事天经地义，虽然雇佣兵信誉高于一切，但面对足以威胁到自己性命的买卖图洛普还是数次想撒手不干。可是图洛普都坚持了次想撒手不干。可是图洛普都坚持了下来，因为他与这位神秘女子之间产生了一种微妙的情愫。当最终的真相来临，所有的人都将面对一场巨大的考验……', 'http://175.6.0.111:8058/200904/29/121509644.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '761', '0', '0', '0', '0', '1', '1', '17', '71', 'qvod', '', 'qvod://438889472|CFDFCAAC36BCCA069DBDC69C67F87307123B4C50|巴比伦新纪元.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1102', '', 'B', '', '6.0', '126', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1038', '8', '史密斯行动', '', '', '', '布拉德·皮特,安吉拉·贝塞特,安吉丽娜·朱莉,文斯·沃恩,亚当·布罗迪', '道格·利曼', '布拉德皮特和安吉丽娜朱莉所扮演的史密斯夫妇在外人眼里是一对令人羡慕的夫妻，他们家境优越，夫妻恩爱。但多年的夫妻生活却多少磨平当初的激情，而多些许平淡与乏味，而这一切都仅仅是假象而已，这夫妇二人都是受过专业训练，身经百战，杀人无数的职业杀手。他们各自效力于对立的两个秘密组织，可能由于双方都是伪装技法高超的超级杀手，虽然同室而居七年有余，但各自却对对方的秘密身份知之甚少。而在一次执行刺杀任务中，夫妇二人恰好被同时指派去刺杀同一个目标，这使得二人为完成任务不得不同室操戈，而结果却是两败俱伤。为了找到导致自己任务失败的对方，夫妇俩开始调动一切资源调查对方的身份，而随着调查的深入，越来越多的证据指向同自己生活多年的爱人，因此，夫妻二人的猜疑和试探也越加深入，直到妻子在晚餐中无意将自己敏捷的身手暴露给同是内行的杀手丈夫……之后便开始了夫妻之间的疯狂杀戮……', 'http://175.6.0.111:8058/200905/04/143316135.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '256', '0', '0', '0', '0', '1', '1', '69', '100', 'qvod', '', 'qvod://502382592|274A1BACA391E28054AF20EB16D7BD08E44BD769|史密斯行动.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1587', '', 'S', '', '4.0', '79', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1039', '8', '秘密雇员', '', '', '', '莫妮卡·贝鲁奇,文森特·卡塞尔', '弗雷德里克·施恩多弗', '四个政府间谍共同去卡萨布兰卡执行一个秘密任务，布利梭(文森特·卡索饰)和两个同事从巴黎出发，与在外执行任务的女同事莉萨(莫尼卡·贝鲁奇饰)会合。莉萨在瑞士卧底多时，她甚至已经以保姆的身份进入了目标人物的家中，与那位俄国商人的两个孩子相处的很不错，就是她拿到那些重要的资料。 阳光明媚，高级旅馆，异国风情，一切看上去都很好。虽。虽然不时有些小插曲发生，但任务进行的还算顺利，在大家的努力之下，他们找到了水下的沉船，拿到了需要的东西，完成了此次任务的目标。但他们不知道，就在同时同地，他们的另一个同行也在执行自己的任务。回国途中，莉萨在瑞士机场被警方拘捕，警方在她的皮包中搜出了一包海洛因。在她身后的布利梭侥幸顺利回到了巴黎，他发觉事情也许不是他们想象的那么简单。就在他刚刚想提醒朋友当心之际，一个同事在寓所被杀。杀手不是别人，正是在卡萨布兰卡海滩对他的同事大献殷勤的异国女子。这一次，他决定追查到底……', 'http://175.6.0.111:8058/200905/04/102841886.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '58', '0', '0', '0', '0', '1', '1', '52', '2', 'qvod', '', 'qvod://318257152|1E893975030FFF21A79B1CA756FE9B4C20BE372D|秘密雇员.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1504', '', 'M', '', '9.0', '84', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1040', '8', '狂沙神驹', '', '', '', '维果·莫天森,奥玛·谢里夫,祖雷克哈·罗宾逊', '乔·庄斯顿', '《狂沙神驹》根据美国西部顶尖赛马手弗兰克·霍普金斯(Frank T. Hopkins)的真实经历改编而成，是一部充满着动作冒险与个人救赎的史诗巨作。 数个世纪以来每年举办的“火的海洋”(Ocean of Fire)是一项极其艰苦的耐力赛马竞技，3000英里的征程即使在平素地域尚且困难重重，何况选择了酷热郁闷而冷酷无情的阿拉伯沙漠，而且参赛赛马必须是由阿拉伯世界中最纯种、最高贵，由最尊贵的皇室家族拥有的赛马组成，但在公元1890年，这一根深蒂固的世俗被彻底的击碎了。 美国人弗兰克·霍普金斯(维果·莫天森饰)曾经是美国骑兵部队一名骁勇善战骑术高绝的骑士国际知名人士的声望除了弗兰克凭借自身不懈努力以外，得力坐骑“海德尔格”(Hidalgo)的鼎立辅助同样是功不可没。如是背景下的1890年，弗兰克首次受邀带着“海德尔格”共同参加这项悲壮的沙漠耐力竞技，一人一马要面对整个阿拉伯世界中最优秀赛马的挑战。 把比赛的艰辛姑且放到一边，公平竞赛的意义也变得越发淡薄，阿拉伯人显然并不能接受外国人的介入，这其中尤以阿拉伯半岛和北非沙漠地区从事游牧的贝督因人最为露骨，他们中的一些骑士暗下决心不惜一切手段来阻止外籍人完成比赛。对弗兰克而言，“火的海洋”不仅仅成为了一场为荣誉与自尊的角力，更是一场为了自己及爱马“海德尔格”的生死存亡的战斗，把不可能完成的任务完成是唯一的选择。结局是令人胆战心惊后的满足，在朋友的帮助下，弗兰克最终完成了比赛，“海德尔格”也成为了阿拉伯耐力竞技历史上获胜的绝无仅有的美国赛马。 幕后 2004年刚刚拉开帷幕，志在必得的迪斯尼就把如此一部精心打磨、明星耀眼的巨作呈现给诸位追求视觉刺激的影迷。9000万美元的预算并非一定代表着制作精良，历来花大头钱办糟心事的电影制片人也是比比皆是，不过《狂沙神驹》似乎另有所求。除各路风光演员加盟外，重头戏应该是工业光魔公司(ILM：Industrial Light & Magic)的沙漠风暴场景电脑特技效果。《木乃伊》所留下的沙漠视觉冲击力使人对其的创作能力满怀信心，有所突破而又不哗众取宠是问题的关键。 以“马”为本最近又在好莱坞电影市场上重放光彩，从梦工厂2002年的动画片《小马王》的成功，直至近期由《奔腾年代》的角逐奥斯卡最佳影片奖渐进步入的高潮，处处彰显商机一片，因而才会引得无数英雄竞相“折腰”之举。担任本片编剧的约翰·福斯克对马的感情似乎超乎寻常的深，白点也可以叫做轻车熟路，不为什么，只因《小马王》的前车良鉴。导演乔·约翰斯顿的作品中尽管《侏罗纪公园Ⅲ》、《亲爱的，我把孩子变小了》等特技占绝对优势的影片多了些，不过这恰恰决了其出任本片导演的其分之处。无视导演的用心毕竟不道德，《狂沙神驹》的先期人气反映证明了迪斯尼的高明用人，更是使乔&#8226;约翰斯顿对在春季票房榜上的有所作为野心大增。 走下圣坛 看惯了维果在《魔戒》中的古装扮相，许多人第一眼看到《狂沙神驹》的海报多少还真有点落差感。卸下盛装的阿拉冈变成了一个不折不扣的西部牛仔，面色憔悴但矫健的身姿犹在。维果的骑马技术始学于阿根廷，在《魔戒》中得以完善并升华。从《魔戒三部曲》中走下的维果希望尽早摆脱阿拉冈对自己的束缚，以开拓戏路寻求新的发展。《狂沙神驹》的萌动激发了他的雄心，也希望借此能够成就一颗真正的票房明星。 昔日重现 一位对电影如数家珍的圈外朋友在乍一知晓《狂沙神驹》的故事背景与拍摄手法时，猛然发出了某种的共鸣，这是对《阿拉伯的劳伦斯》的共鸣。同样的大环境，同样的欧洲同路人，更为彻底的是《狂沙神驹》剧组居然成功力邀了当年曾在《阿》片中扮演过Kharish的71岁高龄的奥马·谢里夫再战“江湖”，出演一位阿拉伯酋长。 《阿拉伯劳伦斯》成就了谢里夫，不过，当年的奥斯卡奖的提名与荣膺当时(1963年)的金球奖“最佳男配角奖”显然并没有让谢里夫飘飘然，一种平常心加上不断追求突破的精神最终促成了3年后的又一部典藏作品的问世，它就是《日瓦格医生》。俄罗斯没落贵族日瓦格为谢里夫赢得1966年金奖“最佳男主角”奖，这是对勤勉的一种褒奖，至此，谢里夫的演员生涯达到了无法逾越的高峰。另记，出生于埃及的谢里夫远非80几部电影中演员经历所能涵盖的了的，这位曾经也纵横过桥牌世界的老人的编剧本领同样毫不逊色，十几部电影剧本的创作经历更是让人肃然起敬。 绿叶不绿 祖雷克哈·罗宾逊是生于伦敦的新生代演员，泰国与马来西亚的成长经历使他更容易接近亚洲人，不多的演出历程是更加珍惜眼前机遇的原因。现比之下，不惑之年的亚当·阿里克斯·梅勒更不自然，虽也曾在诸如《人工智能》等名声显赫的影片中有过演出，但这毕竟无法解释从影多年而无一奖项提名乃至获奖的尴尬，可能他注重的并不是虚名的各色杯杯牌牌，自己的满足更重要。马尔科姆·麦克道威尔唯一的一次提名还有追溯到1972在金球奖上的“动作类影片最佳男主角奖”，从此再无骄人建树。 典型好莱坞模式的结合体；一个孤独的英雄，恶劣的自然环境背景，人与动物之间的互助共存，精彩激烈的竞技比赛……“男人”电影？也不尽其然。更像是一部亮丽的风光地貌之旅，把一个人从相对文明的，恶劣的自然环境背景，人与动物之间的互助共存，精彩激烈的竞技比赛……“男人”电影？也不尽其然。更像是一部亮丽的风光地貌之旅，把一个人从相对文明的西部世界直接扔进野蛮荒芜的沙漠。似是而非的《阿拉伯的劳伦斯》，青出于蓝的《木乃伊》，值得期待！', 'http://175.6.0.111:8058/200904/30/174420049.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '654', '0', '0', '0', '0', '1', '1', '30', '83', 'qvod', '', 'qvod://302432256|4A7DA2584334351135A0EF270B5A6033D503E353|200812_沙漠骑兵.狂沙神驹.2004.DVD-RMVB-CD1.rmvb|\rqvod://275236864|A75D09D5F52DCC650D49E9AACA360584C1130400|200812_沙漠骑兵.狂沙神驹.2004.DVD-RMVB-CD2.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1446', '', 'K', '', '6.0', '7', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1041', '8', '绝不低头', '', '', '', '大卫·阿奎特,DMX,Michael,Ealy', 'Ernest,R.,Dickerson', '金·大卫(DMX饰)是一个稍显固执，个性鲜明的罪犯，他返回家乡试图用自己的行动为过去的过错赎罪，但遗憾的是，迎接他的是从未改变的暴力和他自己的死亡。但他的最后时刻并不孤独，他认识了保了保罗(大卫·阿奎特饰)，虽然他和这个有远大抱负的记者仅仅呆在一起几分钟，但是他却影响了大卫的一生。 金·大卫曾经是整个街区最聪明的家伙，他靠自己的手段获得了成功，而他内心深入仍隐藏着善的种子，他把自己奋斗的故事录在了磁带上，叙述自己曾经所犯过下的罪行，并且揭示了暴行必然得到偿还的轮回，现在，他的经历证明了这种轮回在他身上又一次得到了印证……', 'http://175.6.0.111:8058/200905/05/143203894.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '686', '0', '0', '0', '0', '1', '1', '79', '4', 'qvod', '', 'qvod://447336448|C4841E85D8538086A423ECDDDDA6B0B43CFC5427|绝不低头.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1791', '', 'J', '', '9.0', '158', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1042', '8', '大漠雄风', '', '', '', '维果·莫特森,马尔科姆·麦克道威尔,祖雷克哈·罗宾逊', '乔·庄斯顿', '几个世纪以来每年举办的“海洋之火”(Ocean of Fire)是一项极其艰苦的耐力赛马赛，3000英里的征程，更何况是在酷热郁闷、冷酷无情的阿拉伯沙漠。参赛赛马都有严格的限制，它们必须是阿拉伯世界最纯种、血统最高贵的马，并且由最尊贵的皇室家族所拥有。 但在公元1890年，一位富有的阿拉伯酋长开了先河，首次邀请一位美国人携其坐骑参加了该年度的比赛。弗兰克-T-霍普金斯(Frank T.Hopkins，维戈-莫特森饰，Viggo Mortensen)是一位美国西部牛仔，他曾经是美国骑兵部队的一名骑术高超的通信员，被西方国家公认为最顶尖的骑手。 酋长(谢里夫饰，Sharif)坚持要让这位美国牛仔及其坐骑“海德尔格”(Hidalgo)参加这场赛事，同世界上美国牛仔及其坐骑“海德尔格”(Hidalgo)参加这场赛事，同世界上最优秀的阿拉伯赛马及贝多因骑手交锋。而一些贝多因人却暗下决心不惜一切代价阻挠外籍人参加这场比赛。对于弗兰克而言，“海洋之火”已不仅仅事关本人的骄傲与荣誉，同时更与自己的生死休戚相关。为了生存，他和他的坐骑\"海德尔格\"必须全力以赴，去试一试这场似乎不可能取胜的赛事。', 'http://175.6.0.111:8058/200905/05/121040538.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '321', '0', '0', '0', '0', '1', '1', '85', '9', 'qvod', '', 'qvod://1221893120|1B5532AF007BF00E994BDA38BB27B2959CADE8D6|[大漠雄风][720p.cn]_01.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1758', '', 'D', '', '4.0', '39', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1043', '8', '环游世界80天', '', '', '', '成龙,阿诺·施瓦辛格,史蒂夫·库根', '弗兰克·克拉斯', '故事开始于1873年的伦敦，中国人路路通(成龙 饰)为了追寻村里丢失的一尊翡翠佛像，辗转流浪到了英国，反被夺走佛像的坏人追杀，他只好寻找机会庇护在他的朋友、有点神经质的发明家和冒险家斐利亚·福克(史蒂夫·库根 饰)的家中。他们以前曾经在路上相识后，受彼此的冒险气质吸引，两人成为了好朋友。 福克这位爱好发明的英国贵族绅士，最近发明了一种飞行器，他夸口说能用它环绕地球一周。这个古怪的想法，被他的贵族朋友和英国改良俱乐部认为是痴人说梦，地球如梦，地球如此之大要环绕它走一圈绝对不是这么简单的事。气激之下，福克用自己的财富和名誉下了一大赌注，他打赌在80天时间内，利用火车、轮船、大象和热气球等方式，环绕地球一周。 为了赢得这个赌注也为了自己的荣誉，福克邀请他的中国朋友路路通担任助手，路路通也可趁这此环球旅行把夺回个佛像运送回中国。于是，他们开始了这个史无前例的冒险之旅。在环游世界的路上，他们遇到了种种的磨难，在刚出发时就被警察追踪，原因仅仅是当福克和路路通离开伦敦的时候，那里恰好发生了一起银行抢劫案，而福克与抢劫嫌疑犯又是长得如此像。 除了警察一路的追踪，他们在半路上还遇到了各种奇怪的人，有靠旅行获取灵感的美女艺术家，风趣的热气球工厂师，还有梳着面条爆炸头的土耳其王子(阿诺·施瓦辛格饰)和古怪的发明家以及飞机发明人莱特兄弟。当然啦，最有意思的是，他们在路上邂逅了福克未来的妻子、美丽的莫妮葵(塞丝·弗郎斯 饰)。 当然，一路上还遭遇到了许多更加惊险刺激，也更加好玩的事情。他们在印度遇到了野人差点被人生吃，还在大海上遇到了巨大的风浪几乎船覆人亡，此外还有土著的打劫，他国人的挑衅等等。直到他们真的环游世界一周，他们经过巴黎、土耳其、印度、中国和美国，最后终于返回到了英国伦敦……', 'http://175.6.0.111:8058/200905/04/145715342.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '466', '0', '0', '0', '0', '1', '1', '2', '40', 'qvod', '', 'qvod://387268608|6C76422F2D8D052C110C96DCAE24C24AFC399889|【mfdyy.com免费电影院】【成龙电影】环游世界80天.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1606', '', 'H', '', '1.0', '144', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1044', '8', '雷鸟惊航', '', '', '', '比尔·帕克斯顿,本·金斯利,布拉迪·科贝特', 'Jonathan,Frakes', '在位于亚热带的南太平洋的浩淼烟波中，隐藏着一个无人知道的葱郁小岛，它的名字叫“特雷西”，这个名字来源于它的主人，今年65岁的杰夫·特雷西(比尔·帕克斯顿饰)，一名退休的宇航员和亿万富翁。他买下这个坐标为TB5的小岛，同时在这里创建了一个国际秘密救援组织，他是这个组织的领导者，一旦有哪里发生军队和普通救援队无法胜任的险情，他就和自己的四个儿子驾驶着特制的高科技飞船前往出事地点实施救援。 阿兰·特雷西(布莱德·考拜特饰)是杰夫?特雷西最小的儿子，在这个英雄家族里，他总有种被冷落的感觉。1991年出生的他今年已经19岁了，可当父亲和哥哥们忙于拯救世界的时候，他却还会被寄宿学校的升级学分搞得焦头烂额，他也不能够对同学讲出那个鼎鼎大名的“雷鸟特攻队”就是由自己的父亲和哥哥组成的，因为至今家人都还在保守这个秘密。阿兰梦想着有一天，他能够着有一天，他能够参加这个英雄的团队，能够像哥哥们一样环绕世界飞行，并且在人们最危难的时候拯救他们的生命。 这个暑假，阿兰带着自己的两个好朋友，聪明的弗麦特(索伦·福尔顿 饰)和热衷于冒险的丁丁(维尼萨·安·休金斯饰)一起返回了“特雷西岛”，然而他们并没有遇到热情的接待，因为“雷鸟特攻队”接到一个紧急的求救信号，他们火速赶往救援。但情况并非雷鸟们想像的那样，这个求救信号是他们的宿敌，掌握国际犯罪集团的胡德(本?金斯利饰)的阴谋，他正在将雷鸟引入自己布置的圈套之中，然后将他们消灭并将这个基地和先进的雷鸟飞船占为己有。 似乎一切都按照胡德的计划进行着，雷鸟们遭遇到一个又一个诡计，陷入了无力反攻的境地。但就在这个时候，被胡德甚至是雷鸟队员们忽略的阿兰和他的小伙伴们挺身而出，他们发挥了令人难以想像的勇气和智慧，破坏了胡德的计划，拯救了“雷鸟特攻队”……', 'http://175.6.0.111:8058/200905/05/172233594.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '666', '0', '0', '0', '0', '1', '1', '23', '72', 'qvod', '', 'qvod://226873344|B99D757C28C610CB88CFC81DE3B2AD177614C473|雷鸟惊航.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1894', '', 'L', '', '8.0', '124', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1045', '8', '企业战士2：极限挑战', '', '', '', '威廉·贝利,艾洛蒂袁,劳伦·皮蒙泰西', 'Julien,Seri', '一群身怀绝技的法国年青勇士——威谦、阿肯、健治、卢卡斯、洛公、亚基和罗伦，各自拥有罕见的天赋本领，精通飞檐走壁腾空术！他们为了超越自我，决定去泰国曼谷闯天下。在曼谷，勇士们碰上了由华裔帮派的重要成员——恶狼帮，恶狼帮一直替日本黑道做尽坏事。双方交手的过程中，众人更遇上帮主的妹妹——美貌与武功双全的阿姿……恶狼帮开罪了无数江湖人士，阿姿也惨被绑架。这群勇士冒着生命危险，前往敌人基地，直捣黄龙！', 'http://175.6.0.111:8058/200905/07/110511060.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '262', '0', '0', '0', '0', '1', '1', '13', '51', 'qvod', '', 'qvod://586156032|B2228BB94872E5395F7693BF868A5673AD4DF25D|企业战士2极限挑战.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=2186', '', 'Q', '', '6.0', '175', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1046', '8', '风斗士', '', '', '', '加藤雅也,杨东根,平山绫', '梁允浩', '故事描写了韩国跆拳道创始人崔裴达的一生。在日本人统治朝鲜的时代，体弱多病的少年崔裴达向自己家的佣人范沭学习格斗以强身健体。在朝鲜独立运动之后，崔裴达决心要成为一个飞机驾驶员，留学日本。但航空学校与他想像的完全不一样，朝鲜人在日本人的学校里受尽凌辱，在被带刀教官殴打和威胁的过程中，崔裴达回忆起了范沭教给自己的格斗，结合日本的空手道和柔道，他开始创立一种新的打斗方式——跆拳道。', 'http://175.6.0.111:8058/200905/06/172841042.jpg', '韩国', '', '0', '0', '', '1', '1384344240', '960', '0', '0', '0', '0', '1', '1', '29', '49', 'qvod', '', 'qvod://179929088|386EFF83BE3ECF24564B27FAB84677FAB761FBE8|风斗士2_cd1.rmvb|\rqvod://216274944|349A1E68887AEE091014B5CC9075614D8028F4FD|风斗士2_cd2.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=2122', '', 'F', '', '1.0', '168', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1047', '8', '阿罗汉', '', '', '', '柳承范,安圣基,郑斗洪', '柳承莞', '柳乘泛在片中饰演警探Sang-hwan，致力维持正义的世界，但现实世界中他却被坏人打得落花流水。一天，他得七位武林高人指点，与Eui-jin一起继承他们的武功，成为世界的义士Murachi。然而，在世界的另一端，邪恶之神Heug Un逃囚，那么Sang-hwan可否与Eui-jin拯救世界？', 'http://175.6.0.111:8058/200905/06/160312124.jpg', '韩国', '', '0', '0', '', '1', '1384344240', '249', '0', '0', '0', '0', '1', '1', '75', '70', 'qvod', '', 'qvod://646275072|26A9A882A6BDBA964F1A991B3B97AF50B6B531EA|24b_阿罗汉DVD_50B6B531EA.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=2074', '', 'A', '', '4.0', '41', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1048', '8', '深入虎穴', '', '', '', '史蒂文·席格,Ida,Nowakowska,Agnieszka,Wagner', '梁普智', '比利雷是一个潜伏在黑社会的探员，他发现组织正在进行着一项可怕的任务，年轻的少女们成为了“毒品网络”的对象。为了粉碎这场可怕的阴谋，比利雷将深入虎穴，展开一场斗智斗勇的决战……', 'http://175.6.0.111:8058/200905/07/123138166.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '606', '0', '0', '0', '0', '1', '1', '76', '46', 'qvod', '', 'qvod://337061888|D36BB536B496C929F307F625311E2D319F8E2578|深入虎穴_深入虎穴.all.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=2229', '', 'S', '', '9.0', '145', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1049', '8', '亡魂复活', '', '', '', '尚格·云顿,任达华,Philip,Tan', 'Philippe,Martinez', '在自己的妻子被黑帮残忍杀害后，一名隐退江湖的杀手单枪匹马向黑帮发起挑战，在危机四伏中，他还担负起了保护一个特殊身份小姑娘的责任……', 'http://175.6.0.111:8058/200905/11/104058635.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '717', '0', '0', '0', '0', '1', '1', '37', '54', 'qvod', '', 'qvod://240951296|30281EB4FC6B1417B20099E1F2D2E3CD14AD33CC|亡魂复活.rm|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=2548', '', 'W', '', '7.0', '138', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1050', '8', '无间交易', '', '', '', '金·寇兹,韦斯利·斯奈普斯,杰奎琳·奥拉德丝', 'David,Carson', 'Dean Cage(Wesley Snipes饰)，一位以前的特殊的Ops战士，被一个中央情报局代理人绑架并且注射央情报局代理人绑架并且注射使他易受影响控制药。Dean Cage逃跑，并且去在找解毒药，但是首先他必须离开他的敌人，他的内部的魔鬼，并且他受到药的幻想作用越来越严重，或者他将……', 'http://175.6.0.111:8058/200905/08/122727835.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '947', '0', '0', '0', '0', '1', '1', '82', '7', 'qvod', '', 'qvod://314525696|C01A2E3638918BFF995FBAA3E0AAB117F5CFCF88|动作_无间交易.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=2483', '', 'W', '', '7.0', '8', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1051', '8', '永无天日', '', '', '', 'Grayson,McCouch,阿德里安·保罗,Amy,Locane', 'James,Seale', '每天，成千上万人下都是进入地下停车库，驾驶他们的汽车，然后安然到家。但今晚，刚离开公司从五层下到停车处的金融分析员（Grayson McCouch）却回不了家。他刚完成一项夜间生完成一项夜间生意，当汤姆到达第五（E5） 车库，他发现他的汽车起动不了。更糟的是，电梯被关闭了，并且每个楼梯口都被锁了。但是，被困在停车场的汤姆决不会孤单，一些神秘人物也在这里。焦急的等待中，出现了一辆6000 磅怪异的卡车，一个司机就掩藏在染色玻璃后面——他的目的只有一个：不让汤姆活着离开。接下来的六个小时里，一场恐怖的猫捉老鼠的游戏上演了。杀手似乎有意考验汤姆的生存意志，让他感受窘迫的煎熬。汤姆必须智胜他的追击者并且设法找出想要他死的人。难道是他的商务伙伴Gavin ，Adrian ？他的妻子Molly ，Locane也会有份？抑或是简单的报复在他早些时侯得罪某人之后？当他冲破层层困难赶到车库最上层，他发现他能活到现在是因为那个司机故意有所保留。究竟是汤姆智胜这个神奇的凶手。还是他不会生还看到明天的太阳？', 'http://175.6.0.111:8058/200905/13/140433701.jpg', '欧美', '', '0', '0', '', '1', '1384344240', '316', '0', '0', '0', '0', '1', '1', '66', '14', 'qvod', '', 'qvod://263267154|3FB541E31CEB35C4D8B1871181CAD6E8A7469371|永无天日.rm|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=2835', '', 'Y', '', '7.0', '178', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1052', '10', '过界', '', '', '', '郭涛,李晓冉,郑则士', '袁卫东', '顾涛（郭涛 饰）身为某汽车公司的高级营销主管，原本是一位颇为成功的都市“精英”。某日，与“偏执狂”型的客户（郑则士 饰）遭遇，因为受不了对方近乎“变态”地挑剔车辆身上的一个小瑕疵，顾涛最终爆发，失手打死了客户。这一突如其来的变故，，改变了顾涛全部的生活状态，仓惶间，他与妻子小安（李小冉 饰）开着那辆带着瑕疵的汽车，一路“亡命天涯”。 奇异俏丽的小安，有着让所有男人都怦然心动的魅力，然而几年的共同生活，却让这对都市金童玉女之间出现了深深的沟壑。随着越来越远离都市回归自然，两人之间逐渐干涸的爱情和欲望又再次蓬勃，而随之而来的还有埋藏在内心深处的疼痛。 一路的逃亡，一路的追随，顾涛和小安在路上不断遭遇着分离和相聚，也不断与各种各样的人交错而过，而最终等待着他们的，却是一个无论如何都难以想象的结局：秘密原来一直都藏在他们的那辆座驾中，一路与他们如影随形。', 'http://175.6.0.111:8058/200903/27/190547456.jpg', '大陆', '', '0', '0', '', '1', '1384344259', '472', '0', '0', '0', '0', '1', '1', '61', '29', 'qvod', '', 'qvod://378796301|6876D9CCD23C331503C56A3B4CD63D8A2E3627A6|过界_过界.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=40', '', 'G', '', '6.0', '182', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1053', '10', '黑心母女', '', '', '', '沈惠珍,金秀美,李相宇,李多喜,李季仁', '赵南浩', '自称驾驶技术不比男人差的三十八岁离婚女人南熙（沈慧珍饰）靠开着大卡车卖水果为生活，临近老年却总是盘算着找个男朋友的妈妈（金秀美饰）作为帮手，而二十岁的女儿羽（李多喜饰）则偶尔打工做着成为明星级广播员的美丽白日梦。一日，四处回转叫卖水果的南熙不小心撞倒了样子看起来纯纯的男青年俊（李相宇饰），着急的南熙问受伤的他家住在哪里好送其回家，但俊怎么都不肯说，于是南熙只好把他带回家。三个女人与一个男人的同居生活从此开始了。新鲜成员的加入，令这原本寂寞的一家顿时热闹成了一锅粥：感觉自己在俊面前似乎回到了少女时代的外婆，不喜欢说话与家人有代沟与年龄相近的俊成为好友的羽，以及察觉自己荒废了许久的心重新渴望爱情的南熙，三个女人格格不入的心开始慢慢地向彼此的方向靠近，俊用其身上特有的亲和与脸颊绽放出来的温暖笑容使得三个女人原本无颜色的心染上了似彩虹般美丽的颜色……还有一直单恋着南熙的街坊郑大叔（李桂仁饰），众人发生了一连串的趣事。BR 像变魔法一样将三母女变成真正的家人的爱的身姿的俊在某天突然坚持要离开，三个女人当然不舍得，为了能再见到俊，她们制定了神奇的秘密行动计划……', 'http://175.6.0.111:8058/200903/31/174751785.jpg', '日本', '', '0', '0', '', '1', '1384344259', '521', '0', '0', '0', '0', '1', '1', '4', '57', 'qvod', '', 'qvod://455875301|D1B308365173591C8D5AFD2505971B1604C2058D|黑心母女.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=122', '', 'H', '', '1.0', '186', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1054', '10', '超感觉情侣', '', '', '', '秦久,朴宝英', '金亨周', '拥有特异功能的男人VS天真少女的约会！ 能够看懂别人内心想法的秀民（秦久饰）活在安静的一个人圈子里，性格忧郁，突然有一天，智商高达180的活泼少女贤真（朴宝英饰）出现在他面前。自从两个人第一次相遇后，秀民的生活就开始闹腾起来。 秀民用尽很多办法都不能把这个整天恶作剧给自己添麻烦事儿的贤真甩脱，不可思议的是， 读懂别人内心从未碰壁的秀民也有参不透古灵精怪的贤真在想些什么的时候。渐渐地，发现秀民有特殊能力的贤真开始谋划训练前者的能力达到新的境界：做了一次次地测试。起初觉得贤真很烦人的秀民察觉到她可爱善良的一面，并且渐渐地被其吸引住。 两个人开始运用秀民的特异功能撮合一对对情侣，在这过程中，贤真和秀民也产生了感情，准备在游乐园进行拍拖的第一次浪漫约会。没料到在这里碰见了诱拐事件的犯人。有着旺盛好奇心的贤真连忙怂恿秀民用恿秀民用超能力追捕前者的踪迹，于是，史上最荒唐也最罗曼蒂克的跟踪拐骗人的约会行动正式拉开了序幕……', 'http://175.6.0.111:8058/200903/31/161228616.jpg', '日本', '', '0', '0', '', '1', '1384344259', '977', '0', '0', '0', '0', '1', '1', '37', '36', 'qvod', '', 'qvod://374390121|B55CB88908DBEFEA0201D7B2E058FA499566AF74|超感觉情侣.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=116', '', 'C', '', '1.0', '158', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1055', '10', '回家的路/疯狂直播秀/听妈妈的话', '', '', '', '杜汶泽,范冰冰,尹晓洁,彭波', '方刚亮', '回家的路选取时下最流行的真人秀电视节目作为故事背景，讲述了范冰冰扮演的女主播田聪为了提高栏目收视率，拿儿子和前夫作秀的故事。 节目主持人是人们心中非常时尚的一个职业，打扮得光鲜亮丽在舞台上、节目中展现八面玲珑，特别是美女主播更是令无数女孩心生向往。但是作为职业女性，为了事业所付出的辛苦和代价，华丽灿烂的舞台背后的辛酸苦辣却少有人知。回家的路将为人们解读这个职业之外鲜为人知的故事。 范冰冰在回家的路中当上了真人秀女主播田聪，为了自己主持的一挡电视栏目回家的路把自己的儿子拉来参加，儿子在节目录制中走丢，而小路笑最大的愿望是去东方明珠上找妈妈，在他走丢的途中遇到了由尹晓洁饰演的空姐李昔。李昔之前通过电视直播了解到路笑的愿望，有了咖啡馆与杜汶泽实验的路路父的相遇，并对路父的执著为之感动，当李昔意外遇到路笑时把对路父的情愫以及对失去母爱孩子的疼爱全部灌注到小路笑身上，李昔带着路笑踏上寻找妈妈的路，而在途中两人相处的时间里，路笑慢慢觉得这个空姐就像是自己的妈妈……', 'http://175.6.0.111:8058/200904/15/180252149.jpg', '大陆', '', '0', '0', '', '1', '1384344259', '7', '0', '0', '0', '0', '1', '1', '83', '24', 'qvod', '', 'qvod://473262080|5B8387484E258A37EB2491E127910B51AE50FEFA|回家的路_回家的路.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=411', '', 'H', '', '9.0', '55', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1056', '10', '伊丽莎白镇', '', '', '', '奥兰多·布鲁姆,克尔斯滕·邓斯特,苏珊·萨兰登,亚历克·鲍德温,布鲁斯·麦克吉尔', '卡梅隆·克罗威', '27岁的德鲁（奥兰多布鲁姆）有着众人梦想的成功——一家体育用品公司的高级运动鞋设计师，一个待他如子的老板，还有一个漂亮美艳的女朋友。当然，生活总不会这么顺风顺水，这不，他马上就要经历了一次我们大部分人甚至在噩梦中都不曾想到的事业危机——经过8年设计完成的完美运动鞋被召回，这个灾难将使公司损失9亿7200万美元 面临严重的失败和接近终生事业的毁灭，德鲁被解雇了，和他是同事的女友也果断地甩了他，德鲁只剩下一个念头：自杀。这时，一个电话阻止了他马上就他马上就要成功的自杀行动——等待他的是一个更糟的消息，德鲁的父亲，在肯塔基州探望亲戚时去世了。因为是家中独子，而姐姐也有孩子要带，他必须马上赶回老家肯塔基州伊莉莎白镇参加追思会。 然而在旅途中，沮丧的德鲁遇见了一个叫克莱尔（克尔斯汀邓斯特）的有吸引力的空中小姐，有着坚持不懈的好脾气和亲切友善的性情的克莱尔，让德鲁有了不小的异样。而来到伊丽莎白镇这个完全不认识、不了解、不熟悉的小镇，面对这个他父亲曾经最后停留的世界，德鲁开始重新找寻自我，惊喜总是藏在你不以为意的地方……', 'http://175.6.0.111:8058/200908/10/162834936.jpg', '欧美', '', '0', '0', '', '1', '1384344259', '418', '0', '0', '0', '0', '1', '1', '6', '29', 'qvod', '', 'qvod://239945590|5733984A559C35FAFE5E39925D0D6AADCE83BEFA|2005伊丽莎白镇CD1.rmvb|\rqvod://255867788|6431A74EDDA40C224B2757E8D496BB5E4A3D6BE8|2005伊丽莎白镇CD2.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=273', '', 'Y', '', '4.0', '60', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1057', '10', '米尼', '', '', '', '刘烨,李心洁,张耀扬', '陈苗', '主人公米尼李心洁饰演在海南长大，是知青后代，倔强而乐观。自幼接受严格杂技训练，长大后成为了一名出色的空中飞人演员，是上海大世界杂技团的主角之一。由于父亲早亡，母亲弃她而去，寄居舅舅家，极其渴望相依为命、能带给她生命飞升的爱情。 阿康刘烨饰演是一家名叫 “随波逐流”的音像制品店的店员。或许是因为守寡的母亲从小对他过于严厉，长大后的阿康是个性格反叛，轻视社会规范的人。米尼和阿康虽然生存在不同的环境里，却同样生活在空虚迷惑的精神世界中。一次偶然的邂逅，终使他们不能自控地走到了一起“随波逐流””。 阿康的出现，点燃了米尼纯真的爱情。与阿康一同飞车在夜晚空旷的街道，眩晕而迷幻，让米尼的心飞了起来，仿佛他们可以就这样一直紧紧依偎在一起，永远走下去，没有尽头。他们共同憧憬富于诗意的未来，渴望改变自己的生活。然而，他们心中炽烈的爱情在别人的眼中却并非如此美好。', 'http://175.6.0.111:8058/200904/02/183925262.jpg', '大陆', '', '0', '0', '', '1', '1384344259', '147', '0', '0', '0', '0', '1', '1', '88', '51', 'qvod', '', 'qvod://370644116|F2D1234E6A16A419C8DCF4D7F3006F0B76C6FE6F|米尼_(qvodzy.com.cn)01.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=271', '', 'M', '', '2.0', '64', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1058', '10', '女人本色', '', '', '', '梁咏琪,林子祥,薛凯琪,张敬轩,聂远', '黄真真', '繁华都市，在信、晓彤和毛毛，三个女人是好朋友，生活态度却各有不同。 身为高级白领的在信愿意做恋人背后的助力，和丈夫杜林在一个公司工作。然而，当她们的小孩刚满一岁时，杜林却因为公司布局转型，而被突然裁掉。杜林选择去新加坡创业，让在信留在原来的集团，在信有些寂寞。 新上任的CEO程必聪对在信表示出了浓厚的兴趣，老邻居家的学枫也一直对在信表示关心和好感。在信聪明应对，装作不解两人的情意，经常打电话给在新加坡的杜林联系感情。 晓彤相信自己年轻貌美，这个世界都等着她去征服，公司附近的花店店员的追求，她不屑一顾。她在一次被人抢包的时候，遇到一个帅哥造型师英雄救美，并从此变得更加美丽时尚。 毛毛不停地谋求创业发达，不知道是匆忙创业不及顾忌感情，还是为了逃避感情才匆匆忙忙。 经济危机忽然爆发，公司面对巨大压力，在信虽然没有正面接受程必聪的感情，此刻却愿意与这个让自己有些心仪的男人共同对抗压力。 事业下滑而回港的杜林，在窗口看到程必聪送在信回来，敏感地察觉出一些暧昧，忍不住心中抑郁。不久，杜林得知在信用名下财产给他的负债做担保，愧疚与感激交杂，忍不住借酒浇愁，导致出车祸身亡。 独身后的在信有些伤感，程必聪乘机加紧了攻势，在信一度意乱情迷。 晓彤介绍自己的男友给在信认识，那男人却偷着挑逗在信，被在信痛斥后，他恶人先告状，晓彤信以为真，宣布与在信断交。却终于发觉，被在信猜中，自己被人偏财偏色了。 在信决定与程必聪的感情进一步发展，却突然发觉自己被裁员了，而且是为程必聪当了替罪羊，程必聪却不肯程必聪却不肯为她说句话，在信决定忘掉这段感情。 此时，在信的幼子却因为感染sars夭折，在信的情绪落到谷底，准备自杀。 毛毛却意外出现，告诉在信，当初在信投资给毛毛的钱，如今已经赢利。 意外的惊喜让在信忽然惊醒，她决定要做个快乐的女人好好活下去，三个女人重新相聚，开始了创业，而随着她们事业的发展，那些一直静静守护她们的男人们，走入了她们的感情生活……', 'http://175.6.0.111:8058/200904/21/184217961.jpg', '港台', '', '0', '0', '', '1', '1384344259', '581', '0', '0', '0', '0', '1', '1', '19', '49', 'qvod', '', 'qvod://435306496|BD293823E1A1B3184AC79A873BC80AF64058E095|女人本色.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=536', '', 'N', '', '6.0', '157', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1059', '10', '信箱', '', '', '', '张光北,安雅,王岩,熊乃瑾', '张番番', '一段尘封的爱情 一段惊悚的经历 一对凌厉的眼眸在注视 一双看不见的手在操纵 是什么在撕扯往事？ 是谁发来邮件？极度惊恐的背后究竟隐藏着什么秘密？ 当爱与伤怀交织… 男主角郑川是一个滥情于周边女人，为满足自己欲望不择手段的男子。在享受完身边这些或纯洁或本也心怀鬼胎的漂亮女人后，他却陷入到一个又一个令人恐慌的事件当中。郑川三十年前初恋女友此时诡异出现，更让其焦头烂额。而将郑川推向崩溃向崩溃边缘的，则是一切信息都表明这个初恋女友早已撒手人寰。郑川仿佛置身一个恐怖的时空轮回，无法脱身……', 'http://175.6.0.111:8058/200904/22/172705927.jpg', '大陆', '', '0', '0', '', '1', '1384344259', '650', '0', '0', '0', '0', '1', '1', '34', '85', 'qvod', '', 'qvod://397295616|808399DB5C6D58096B1A11CF1762D77A0121CF4C|信箱.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=564', '', 'X', '', '2.0', '123', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1060', '10', '舞女纯情', '', '', '', '文根英,朴健衡,朴元尚,李宰久,尹赞', '朴永勋', '这是一个中国朝鲜族女孩前往韩国参加舞蹈比赛，并在那里邂逅帅哥王子的浪漫爱情故事。 吉林省延边市女孩张彩琳（文根英 饰），像所有的朝鲜族女孩那样，从小就喜欢跳舞，做梦都想有一天能在舞台上展现自己优美的舞姿。彩琳的姐姐是个舞蹈高手，被邀请参加在韩国仁川市举行的“朝鲜族自治州舞蹈选手选拔大会”，想不到姐姐一路比赛一直遥遥领先，冲到了决赛。可是，在决赛前夕，姐姐却因为特殊事情不能参加了。彩琳听说姐姐要退出比赛，感到非常可惜，这个聪明的女孩忽然灵机一闪，冒出一个大胆的想法：自己为何不装扮成姐姐参加舞蹈决赛呢？这个大胆的想法连她自己都吓了一跳。彩琳立刻办理了前往韩国的旅游签证，从延吉出发驶向韩国仁川的轮船里，透过蒙蒙的雪丝渐渐看到仁川港，彩琳不安的情绪慢慢也转变成恐惧的心理。才19岁的她，妆画得像大人一样，尽管已经尽力装扮了自己可还是掩盖不住她的不自然。 来到韩国后，蔡琳见到了她的舞伴、英俊帅气的舞蹈高手罗永塞（朴健衡）。这个大男孩身上的帅气和淡淡的忧郁气质，以及优美优美的舞姿，让彩琳立刻把他当成了少女心中一直渴望出现的那个白马王子。 永塞也把彩琳当作自己的舞伴，还花了不少钱帮她办理了在韩国长期居留的入国手续，练舞时他却惊讶地发现原来彩琳根本不会跳舞！他终于明白原来彩琳是个替身。失望和被欺骗的感觉下，永塞把彩琳卖给了夜总会。夜总会里，彩琳并没有失去希望，与永塞共同度过的短短几日对她而言可是温馨的回忆。想着在练舞室看到的美丽的礼服和鞋子，叮嘱自己一定要坚持住。可是现实对她实在是很残忍，彩琳是否能重新走上舞台，获得帅哥王子的心呢？', 'http://175.6.0.111:8058/200904/23/185135744.jpg', '日本', '', '0', '0', '', '1', '1384344259', '860', '0', '0', '0', '0', '1', '1', '33', '23', 'qvod', '', 'qvod://130420736|02C59BB214CBF5868F5002635DDF46F43B87FFBA|舞女纯情_舞女纯情CD1.rm|\rqvod://136318976|A140B714B666D8D7746FD15A1EECEEFB569FA77B|舞女纯情_舞女纯情CD2.rm|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=633', '', 'W', '', '6.0', '189', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1061', '10', '奇招尽出', '', '', '', '伍迪·艾伦,杰森·比格斯', '伍迪·艾伦', '杰瑞是纽约一名满怀雄心壮志的作家，他对女孩阿曼达一见钟情。究竟该如何处理这 段感情呢。有些不知所措的杰瑞向他的导师求救，请他提出建议。这位导师其实自己日子 也不好过过，是一位正在奋斗多年的艺术家。他决定尽力帮助杰瑞……', 'http://175.6.0.111:8058/200904/30/172846101.jpg', '欧美', '', '0', '0', '', '1', '1384344259', '308', '0', '0', '0', '0', '1', '1', '78', '30', 'qvod', '', 'qvod://458601472|0DCEFF149735E888AED4E88D9A277D5CD94B73AA|[新好娱乐]奇招尽出.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=1436', '', 'Q', '', '5.0', '151', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1062', '10', '茉莉花开', '', '', '', '章子怡,陆毅,姜文,刘烨,陈冲', '侯咏', '茉莉花开是一部反映一家三代女性的爱情与婚姻的故事，发生在三十年代、五十年代和八十年代，以独特视角展现了二十世纪中国的历史变迁和女人的命运。 茉，一个由经营着照相馆的单亲母亲养大的18岁少女，在遇到孟老板后一生的命运发生了改变，她成了明星，同时也怀上了孟老板的小孩，那是上个世纪30年代旧上海的事茉生下孩子不久就被抛弃了，失去了明星的光彩，她为这个私生女取名叫莉，莉一心要离开这个阴冷的家，嫁给了水泥厂工人邹杰，她决定生个小孩，但她偏偏不能生育，只能从福利院抱了一个女孩，孩，取名叫花，莉对生活仍然极度不满，甚至怀疑自己的丈夫与养女有染，丈夫选择了卧轨自杀，这是上个世纪五六十年代的事莉精神分裂离家出走了，花找了个男友小杜，在结婚后怀孕不久发现小杜有了外遇，她坚决地提出离婚并把小孩生了下来，这已是上世纪80年代的事……', 'http://175.6.0.111:8058/200904/24/154852375.jpg', '大陆', '', '0', '0', '', '1', '1384344259', '178', '0', '0', '0', '0', '1', '1', '72', '56', 'qvod', '', 'qvod://266752000|8AD165CDE49234B9BE83C3C190771CB7E5589DCE|茉莉花开_cd1.rmvb|\rqvod://233499648|B6C0C001007825E956E1863B7F7557A88BF001CE|茉莉花开_cd2.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=671', '', '0', '', '5.0', '190', '1', '0', '0', '0');
INSERT INTO `ff_vod` VALUES ('1063', '10', '时尚先生', '', '', '', '方中信,孔维,吕玉来,张静,舒耀宣,尚涛,王雨,张栩洋', '乔梁', '影片将围绕一群在时尚传媒业和时尚圈工作的年轻人展开。 八卦杂志《雄辩》的主编李嘉吾（方中信饰）面临着一次大麻烦：一方面最新一期杂志因为刊登了女明星偷拍照被有关部门勒令销毁，另一方面杂志出版人迫于经济压力（一本八卦杂志是拿不到广告的）要求他收编集团旗下另一本生活类杂志《宝贝》，然后转型去做时尚杂志。 李嘉吾为了提高新杂志《时尚先生》的知名度和影响力，决定以新杂志的名义操办一次“时尚先生新秀大赛”，他深知要想让这次选秀成功，首要任务就是找到一名可以掌控全局，又能吸引眼球的顶尖人物。但因为长期主持《雄辩》杂志，李在娱乐圈和时尚圈都树敌无数，没人肯帮一个“臭名卓著”、“破落潦倒”的主编。就在李嘉吾穷途末路之时，他的昔日恋人，现在已经成为国内顶级明星的叶青（孔维饰）“适时”地出现，并答应帮助他出任“时尚先生新秀大赛”的总评审。 曾经与李嘉吾相恋，但不幸被误伤的叶青，一直以来都对李怀有深深的怨恨，这次对旧情人伸出援手实际上隐藏着一个极端的报复计划，她找到一直暗恋自己的好友、电脑高手符迦宁（吕玉来饰）和外表酷似男孩子的廖宇（张静饰），共同参与。叶青希望借助自己对大赛的影响力以及在李嘉吾身边工作的符迦宁的帮助，对廖宇施以女扮男装，并将推上“时尚先生新秀大赛”的总冠军宝座，然后再在世人面前揭穿廖宇的女性身份。这样就可以让本已陷入窘境的李嘉吾一败涂地再无翻身之日（一次崇尚男性魅力的选秀居然选出了女性冠军）。 选秀比赛如火如荼，在叶青与符迦宁的精心策划与帮助下，廖宇势如破竹向前挺进。感情总是那么奇妙，在他们合力向总冠军迈进的路上，一边厢，随着叶青与李嘉吾多次明争暗斗式的“交锋”，叶青发现在她心底一直打不开的情感心结已经慢慢解开；另一边，暗恋叶青多年的符迦宁逐步在这一次的近距离亲密接触中，更加真实的认识到自己的情感空间。意想不到的是，随着地位的变化，欲望的膨胀，原本天真无知的廖宇也慢慢发生着变化，为了拿到100万奖金，廖宇开始设计暗算选手。在总决赛前，叶青与符迦宁终于下定决心及时“刹车”，无奈行进中的廖宇却像失控的列车一样，再也停不下来了。叶青与符迦宁无计可施，只有准备承担一切不可预知的后果。 总决赛现场，廖宇距离总冠军只差一步，然而骗局却被人用意想不到的方式揭穿了。在幕后操控的叶青与符迦宁被摆上台面，等待接受“最后的审判”。出乎两人的意料，一直以来饱受她们暗算的这个男人——李嘉吾出来帮助他们承担了一切。 “时尚先生新秀大赛”以“失败”收场，准备离开的李嘉吾却因为新杂志的热销而被出版人挽留。曾经不择手段的廖宇没有拿到总冠军，但找了人生的新落点。故事的结局并没有像童话中那样“从此他们过上了幸福的生活”。一个深爱着李嘉吾的女人和另一个暗恋着叶青的男人，却都对这段感情选择了一种“我干杯，你随意”的结束方式……', 'http://175.6.0.111:8058/200904/16/181525219.jpg', '欧美', '', '0', '0', '', '1', '1384344260', '433', '0', '0', '0', '0', '1', '1', '56', '76', 'qvod', '', 'qvod://381083648|B2C258956950C2D520FB7FFA81A6458D97105BB7|时尚先生_all.rmvb|', 'xml_3', 'http://zy.gvod.net/Video.aspx?Videoid=452', '', 'S', '', '8.0', '141', '1', '0', '0', '0');
