INSERT INTO `ff_vod` VALUES ('1450', '17', '天使情人', '', '', '', '蒋怡,白歆惠,金沛辰,明道,杜德伟,加贺美智久', '未填写', '每个人都有自己的爱情梦想，只是在残酷的现实中难以实现。当我们在最伤心无助的时候，也许曾经希望能有一位天使陪我们疗伤，指引我们继续往下走的方向。 Angelina(蒋怡饰演)，一位如天使般美丽的女人，却在爱情中迷失了方向。爱情的伤痛让她迷惘，恍惚中出了车祸。天使Michael(加贺美智久饰演)出现在她的梦中，又化身为她的医生，陪伴她走过这段伤痛。Michael给了她活下去的勇气与希望。但Michael却在此时留下一封信消失了，他说自己有更重要的任务要执行，要Angelina继续帮助别人重拾生命与爱的勇气，就像他在她身旁一样。 于是Angelina成立了「Angel Lover」这个组织，找来了五位男人去帮助那些为情所困、找不到生命勇气的女人重新面对自己的人生。这群Angel Lover的情人，就像一出戏的编剧、导演兼演员，他们陪伴着这些找不到真爱的女子，演出一出又一出浪漫感人的爱情故事。而主角杨天佑(明道饰演)这位乡下来的穷小子却以他最单纯的心，一再的帮助许多人找回真爱。再的帮助许多人找回真爱。', 'http://175.6.0.111:8058/200906/26/145230354.jpg', '港台', '', '0', '0', '', '1', '1384344478', '758', '0', '0', '0', '0', '1', '1', '2', '76', 'qvod', '', 'qvod://211033488|59671BB8E6FFE835C036D2340C79BBC3E0847E97|天使情人-01.rmvb|\rqvod://194285918|A98290CBEA6B92D6EE07618070546FBCDCCFA86C|天使情人-02.rmvb|\rqvod://148120934|B7602EAB9A27FAF64FD3670678FC229C77D45D7A|天使情人-03.rmvb|\rqvod://189366901|FE659C593AEEFB76E8175FC00E6C4D4842CF44AB|天使情人-04.rmvb|\rqvod://170437120|2280BABCF7D4525785179CF8C6E5211031308938|天使情人-05.rmvb|\rqvod://180853792|72A68C413391C2FCDB322D5521A8F8D5824ED33F|天使情人-06.rmvb|\rqvod://170630858|1C1579D6E8ADA470C5E96823C6DFBA5B8CE66CBF|天使情人-07.rmvb|\rqvod://172429055|6266BA120616369C473113653FD9A225906B0590|天使情人-08.rmvb|\rqvod://181492930|E622D4C32C7BA3AE2362B3D3CA1738A6E95B1FB8|天使情人-09.rmvb|\rqvod://169617477|D9B5B1FF60390A3E5FCE159D7F8D550C216369A8|天使情人-10.rmvb|\rqvod://171980991|1E8BA165251CC8261E5633EA7FF1C93570CBFE40|天使情人-11.rmvb|\rqvod://172420435|FE86A0812A0B983B795A8B66FAB89B3F6159E233|天使情人-12.rmvb|\rqvod://189668920|421D194CDB4039E06201EBEECD85AEC4DA46F92A|天使情人-13.rmvb|\rqvod://170605368|0079E610423408D257784EBDFA1FFF9A210E56A5|天使情人-14.rmvb|\rqvod://171650534|B10C9631AF33F8B51A82FDCCC08F4AB0D89363EB|天使情人-15.rmvb|\rqvod://165059251|7C1544387AB3076B19CB42BE99FDC1A79A466B19|天使情人-16.rmvb|\rqvod://173204573|77A04DD774A7232AE9E34CD3DE5FB8F30BDB8E41|天使情人-17.rmvb|\rqvod://175618234|472A4E3E406179A16695EBB377BF98275