INSERT INTO `ff_vod` VALUES ('1477', '17', '极速传说', '', '', '', '李威,杨丞琳,刘亮佐,张孝全,隋棠,蔡裴琳', '未填写', '知名的汽车代理企业千金Juliet杨丞琳饰与日本ASUKA企业总部总裁渡边司（周群达饰）即将举行结婚典礼，却因小风（李威饰）的出现使Juliet在婚礼的典堂上飞奔逃婚，一段情仇怨由此开始 Mars张孝全饰）是Juliet的哥哥，为了复仇开始赛车并且和其杀父仇人的赛车手太极的赛车程式和成绩一模一样，为了杀父之仇不惜将妹妹推向仇人的儿子的怀里，并以达到复仇的目的。可是却将仇恨、赛车、友情溶于一体，却命丧赛场 渡边司因在结婚礼堂即将完婚的时候被小风将新娘抢走，将全部仇怨汇于小风身上展开复仇计划，小风想以赛车冠军为目的，向爱情展开进攻 小风与Juliet的恋情因此不断被渡边司的仇怨及小风儿时青梅竹马的蜻蜒（蔡斐琳饰）的感情而冲击造成一波多折，还因为两家世仇变的困难重重，Juliet与小风这对现代罗密欧与朱丽叶渐渐步入往日仇恨和现代陷阱之中，甚至让丽叶渐渐步入往日仇恨和现代陷阱之中，甚至让他们面对放弃梦想的生死抉择', 'http://175.6.0.111:8058/200907/17/144632188.jpg', '港台', '', '0', '0', '', '1', '1384344481', '693', '0', '0', '0', '0', '1', '1', '55', '50', 'qvod', '', 'qvod://224995125|1E4EE16D299F30DA729CF81EEEE23FC8C337FB1A|极速传说_01.rmvb|\rqvod://187337195|A8B8FF95E88BFFF33C31B0F1EF25CE732206FB97|极速传说_02.rmvb|\rqvod://181258413|8167CC89F26B9915792D7C9885F123E605419330|极速传说_03.rmvb|\rqvod://181379894|BCBF7804FD23B57235E463DCE871601D13F414F4|极速传说_04.rmvb|\rqvod://185299032|C998CC67B9B8762C2DC9B287A0772870D2200295|极速传说_05.rmvb|\rqvod://179872112|F6C762358AAB9FF848FD030515CDF5DCBD3312AA|极速传说_06.rmvb|\rqvod://176887901|85CC2E31AEE7F318F6B0D459E6188AB4B2206B49|极速传说_07.rmvb|\rqvod://175683791|58496F41D4B8A3DB03A721629911B1EBECD9662F|极速传说_08.rmvb|\rqvod://193166179|79A223ABD5040D37C60D5DF9437E0FD25AC6B918|极速传说_09.rmvb|\rqvod://178543359|6DF72F4232E4B5C7EF1B4169646DEDBD65D7286D|极速传说_10.rmvb|\rqvod://182786061|AB94539FED905CD7C6ED940B0C636A3A100DF29A|极速传说_11.rmvb|\rqvod://181741466|D05C88123E01B9A635A761F0733E577CB1A7830C|极速传说_12.rmvb|\rqvod://183857040|74E84008BD6AD7459B88B01566C9D717CCB07642|极速传说_13.rmvb|\rqvod://174614591|8EC870FEBFEFD3B61A42DC75A2A29E5B21E300DA|极速传说_14.rmvb|\rqvod://173762999|98457B76D9B9AA691FEABD391AFA5742BFD787BF|极速传说_15.rmvb|\rqvod://188640196|77B3C6E2F1A22341F6A2B783A631EEC8F9B2285C|极速传说_16.rmvb|\rqvod://176364782|7BCB3313F3975743FD1A779AEA404748827F9C63|极速传说_17.rmvb|\rqvod://194462420|D3E6C3AA49D3797E8EB473AAE3A5BFFF81B4A5FF|极速传说_18.rmvb|\rqvod://192210622|032A55