INSERT INTO `ff_vod` VALUES ('1498', '17', '泡沫之夏', '', '', '', '何润东,徐熙媛,黄晓明,沈建宏,林嘉绮,吴亚馨,房思', '江丰宏', '夏沫和洛熙都是孤儿，少年的他们在养父母家相识，因为童年留在内心的阴影，他们彼此充满戒备和防范。洛熙在夏沫和弟弟参加电视歌唱大赛遇到尴尬状况下为他们解围，两个孩子中间的坚冰在逐渐融化，也逐渐互相吸引。然而，深爱夏沫的富家少爷欧辰为了分开两人，强迫把洛熙送到英国留学，他的不择手段，终究无法强留夏沫，还将彼此距离推得更远，连记忆都丧失…… 五年后的洛熙成了拥有无数FANS的天王巨星，夏沫与他再次相遇，在演艺圈中两人再续前缘，夏沫与洛熙的发展耀眼动人、令人忌妒羡慕。但这看似美好的世界，随着欧辰恢复记忆而崩塌，他不顾一切要抢回夏沫，不仅封杀洛熙让他失去表演舞台，更以小澄的生命威胁夏沫。 为了弟弟，夏沫放弃了爱情，可是在夏沫与欧辰婚礼的当天，绝望的洛熙竟选择了自杀，而无意间得知姐姐结婚真相的小澄，在上手术台的前一刻坚持拒绝接受欧辰的帮助。悲痛于洛熙的自杀，愧疚于欧辰的深情，本就备受内心煎熬的夏沫，再也无法接受小澄病逝的打击，支撑不住的她，陷入了与世隔绝的自我封闭状态…… 欧辰的深情以及洛熙的痴情，能否挽救心如死灰的夏沫？令人无法呼吸的炙热情感，纠葛动纠葛动容的爱恨情仇，即将华丽上演。', 'http://175.6.0.111:8058/201010/29/20101029110741.jpg', '港台', '', '0', '0', '', '1', '1384344483', '920', '0', '0', '0', '0', '1', '1', '52', '10', 'qvod', '', 'qvod://210102748|E7A1836466DE5833A49F28BD91D7107E4CE108FF|泡沫之夏_2010_DVD_01.rmvb| \rqvod://209741717|57A1007F6D5933DF5794EB41A93EFBDA9EE1B748|泡沫之夏_2010_DVD_02.rmvb| \rqvod://209931655|EA5990270C9E7E953931B822DC002A115D2F85CE|泡沫之夏_2010_DVD_03.rmvb| \rqvod://210162664|B546DB2D168A0CBBD94BF5656F123D713C4C94C0|泡沫之夏_2010_DVD_04.rmvb| \rqvod://209393250|958F92DAE458A7F418864C351E702378FE5B0FCB|泡沫之夏_2010_DVD_05.rmvb| \rqvod://209771380|E5B5C987EC7F815351A67F028F16362F48175F28|泡沫之夏_2010_DVD_06.rmvb| \rqvod://209467486|E2B53CFE866E15A2D8D04B7DC0DA3DD577FA4F39|泡沫之夏_2010_DVD_07.rmvb| \rqvod://209740501|DDC1754A1E3C8E5E348D15E397E31CF64E6542C5|泡沫之夏_2010_DVD_08.rmvb| \rqvod://210017050|33C037B82F83F057E9BE1AEEAA1CC0F72015CE40|泡沫之夏_2010_DVD_09.rmvb| \rqvod://209740013|1A9269985FC6BA44BB2C3B1B84EA985BE74FE88E|泡沫之夏_2010_DVD_10.rmvb| \rqvod://209873751|7CEF503CD0CA48A9CB1717A7042D21757A1F73BA|泡沫之夏_2010_DVD_11.rmvb| \rqvod://214105597|C48B63D9785B4EBD70C6F3E4AB3C34216F01B2E6|泡沫之夏_2010_DVD_12.rmvb| \rqvod://209610238|8B0C099E8DC4E46F1316D775785FD7D5936908B9|泡沫之夏_2010_DVD_13.rmvb| \rqvod://209644324|FAC2E376B504D228F50EECE7C0225806F399F867|泡沫之夏_2010_DVD_14.rmvb| \rqvod://209681307|FA3A4F984470FF655F61C006AEADA91ED1565B2F|泡沫之夏_2010_DVD_15.rmvb|